(1, 'document', 'text/html', 'Главная', '[[!++site_fullname]]', '', 'index', 1, 'data-cls=\"clubStuff\"', 1, 0, 0, 0, 0, '', '[[!loginWay]]\r\n<div class=\"card card-body animated fadeInDown\">\r\n    [[!+rcode:notempty=`\r\n    <div class=\"alert alert-danger\">Error: [[+rcode]]</div>\r\n    `]]\r\n<div class=\"row flex-column-reverse flex-md-row\">\r\n    <div class=\"col-md-8\">\r\n        [[-<h3>Вход для родителей или законных представителей</h3>]]\r\n        <div class=\"loginBlocks\" style=\"display:none\">Вы вошли в систему как <b class=\"user_fullname\"></b>.\r\n            <a href=\"/?service=logout\">Выйти?</a>\r\n        </div>\r\n        <div class=\"loginBlocks\">\r\n            [[!clubConfig?name=`WelcomeHTMLBlock`]]\r\n            <a href=\"/?action=login\" class=\"btn btn-primary showLogin\"><i class=\"fa fa-unlock-alt\"></i> Войти в личный кабинет</a>\r\n        \r\n<div class=\"text-center mx-auto mt-2\" style=\"display:none;max-width:480px\" id=\"loginForm\">\r\n    <form method=\"post\" action=\"?action=login\" role=\"form\">\r\n        [[+errors]]\r\n        <input type=\"hidden\" name=\"returnUrl\" value=\"[[+rurl]]\">\r\n        <input type=\"hidden\" name=\"service\" value=\"login\">\r\n        <div class=\"form-group\">\r\n          <div class=\"form-label-group\">\r\n            <input type=\"text\" id=\"username\" name=\"username\" class=\"form-control placeholder-shown\" required=\"\" autofocus=\"\">\r\n            <label for=\"username\">E-mail</label>\r\n          </div>\r\n        </div>\r\n        <div class=\"form-group\">\r\n          <div class=\"form-label-group\">\r\n            <input type=\"password\" id=\"password\" name=\"password\" class=\"form-control placeholder-shown\" required=\"\">\r\n            <label for=\"password\">********</label>\r\n          </div>\r\n        </div>\r\n        <div class=\"form-group text-center\">\r\n          <div class=\"custom-control custom-control-inline custom-checkbox\">\r\n            <input type=\"checkbox\" class=\"custom-control-input\" id=\"rememberme\" value=\"1\" name=\"rememberme\" checked>\r\n            <label class=\"custom-control-label\" for=\"rememberme\">Запомнить меня</label>\r\n          </div>\r\n        </div>\r\n        <button type=\"submit\" class=\"btn btn-primary btn-block mb-2\">Вход</button><br>\r\n        <a href=\"/login/forgotpassword.html\">Восстановление доступа</a>\r\n    </form>\r\n</div>\r\n\r\n        </div>\r\n    </div>\r\n    <div class=\"col-md-4 animated fadeInRight mb-3\">\r\n        <img src=\"[[!++club_logo]]\" class=\"img-fluid mx-auto d-block\" />\r\n    </div>\r\n</div>\r\n</div>\r\n\r\n<script>\r\n$(function(){\r\n    var user = $(\'#user_fullname\');\r\n    if (user.length>0) {\r\n        $(\'.loginBlocks\').toggle();\r\n        $(\'.user_fullname\').text( user.text() );\r\n    } else {\r\n        function goLogin(e){\r\n            if (e) e.preventDefault();\r\n            $(\'.showLogin.btn\').hide();\r\n            var lgnfrm = $(\'#loginForm\').show();\r\n            clubScroll(lgnfrm);\r\n        }\r\n        $(\'.showLogin\').click(goLogin);\r\n        var param = getUrlVars();\r\n        if (param.action && param.action==\'login\') goLogin();\r\n    }\r\n    $(\'.form-label-group:eq(0) > input\').trigger(\'focus\');\r\n});\r\n</script>', 0, 2, 0, 1, 1, 1, 1649492122, 1, 1668790365, 0, 0, 0, 0, 0, '<i class=\"fa fa-home\"></i> Главная', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'index.html', 0, 0, 1, NULL, 1),
(2, 'document', 'text/html', 'Настройки', '', '', 'opts', 1, 'data-cls=\"clubStuff\"', 1, 0, 0, 5, 0, '', '<style>\r\n    /*header.page-navs{display:none;}*/\r\n    .cp-color-picker {\r\n        z-index: 1051;\r\n    }\r\n</style>\r\n\r\n<div class=\"card card-fluid\" id=\"optsPanel\">\r\n    <div class=\"tab-content card-body\">\r\n        <div id=\"tab_idConfig\" class=\"tab-pane active\">\r\n            <div data-link=\"{include tmpl=\'#tplIdConfig\'}\"></div>\r\n        </div>\r\n        <div id=\"tab_idCity\" class=\"tab-pane\">\r\n            <table id=\"grid_idCity\" data-entity=\"idCity\"></table>\r\n            \r\n        </div>\r\n        <div id=\"tab_idInvoiceType\" class=\"tab-pane\">\r\n            <div class=\"nav nav-pills mb-2\" role=\"tablist\">\r\n                <a class=\"nav-item nav-link active\" data-toggle=\"tab\" href=\"#itype_list\">Услуги</a>\r\n                <a class=\"nav-item nav-link\" data-toggle=\"tab\" href=\"#itype_stype\">Абонементы</a>\r\n            </div>\r\n            <div class=\"tab-content\">\r\n                <div id=\"itype_list\" class=\"tab-pane active\">\r\n                    <div class=\"small mb-1\">\r\n                        <i class=\"fa fa-info-circle\"></i>\r\n                        Списание занятий происходит только если указан тип в столбце \"Абонемент\" и настроены\r\n                        <a href=\"#\" onclick=\"$(\'[href$=itype_stype]\').click();return false;\">правила списания</a>\r\n                    </div>\r\n                    <table id=\"grid_idInvoiceType\" data-entity=\"idInvoiceType\"></table>\r\n                </div>\r\n                <div id=\"itype_stype\" class=\"tab-pane\">\r\n                    <table class=\"table w-auto\">\r\n                        <tbody data-link=\"{include tmpl=\'#tpl_stype_ed\'}\"></tbody>\r\n                    </table>\r\n                </div>\r\n            </div>\r\n        </div>\r\n        <div id=\"tab_idStatus\" class=\"tab-pane\">\r\n            <div class=\"mb-2 d-flex\" data-link=\"{include tmpl=\'#statusTypes\'}\"></div>\r\n            <table id=\"grid_idStatus\" data-entity=\"idStatus\"></table>\r\n            <div class=\"mt-2\" data-link=\"visible{:showArcStatus}\">\r\n                <h6>Архивные статусы</h6>\r\n                <a href=\"#\" data-roinplace=\"CfgInpSave\" data-link=\"text{:cfgArcStatus} data-name{:statusType+\'_arc\'}\" class=\"dashed\"></a>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n\r\n<script>\r\nvar optsData = $.extend({\r\n    opts: {},\r\n    idSchedule: [],\r\n    cS: SCRM.clubStatus\r\n}, SCRM.setClubStatus([[!clubStatus?tbl=`idStatus,idConfig`]]));\r\n\r\nSCRM.link(SCRM.app, {\r\n    topNavTabs: [\r\n        {href: \'#tab_idConfig\', html: \'Настройки\', active: true},\r\n        {href: \'#tab_idCity\', html: \'Города\'},\r\n        {href: \'#tab_idStatus\', html: \'Списки\'},\r\n    ]\r\n});\r\n\r\n[[!+modx.user.id:userinfo=`sudo`:notempty=`\r\noptsData.sudo = true;\r\n`]]\r\n\r\nfunction setConfigValue(key, value) {\r\n    return pEDIT(\'idConfig\', {\r\n        id: key,\r\n        val: value\r\n    }, function(result) {\r\n        SCRM.success(\'Saved\');\r\n        optsData.opts[key] = value;\r\n    });\r\n}\r\n\r\nfunction bindAceEditor(opts) {\r\n    $(\'[data-ace]\')\r\n    .clubAce(opts)\r\n    .removeAttr(\'[data-ace]\');\r\n}\r\n\r\n$.extend(SCRM._service, {\r\n    optsHTML: function(runOpts) {\r\n        SCRM.link(optsData, {\r\n            optsTmpl: \'#tpl_optsHTML\',\r\n            addHTMLBlock: function() {\r\n                var alias = prompt(\'Alias блока (латиница)\');\r\n                if (alias) {\r\n                    var ad = {\r\n                        oper:\'add\',\r\n                        tbl:\'idHTML\',\r\n                        name: alias,\r\n                        alias: alias\r\n                    };\r\n                    pEDIT(\'idStatus\', ad, function(){\r\n                        SCRM.link(SCRM.clubStatus.idHTML).insert(ad);\r\n                        SCRM.link(optsData, {selHTML: alias});\r\n                        SCRM.success(\'Добавил\');\r\n                    });\r\n                }\r\n            }\r\n        });\r\n        if (!optsData.selHTML) {\r\n            $.observe(optsData, \'selHTML\', function(e, data){\r\n                //var d = array2obj(SCRM.clubStatus.idHTML, {}, \'alias\')[data.value];\r\n                var d = SCRM.getClubStatus(\'idHTML\', data.value, \'alias\');\r\n                var cfg_key = data.value+\'HTMLBlock\';\r\n                getConfigFields(cfg_key, function(fields, opts) {\r\n                    SCRM.link(optsData, {\r\n                        optsHTMLEdit: [{\r\n                            key: cfg_key,\r\n                            val: opts[cfg_key],\r\n                            cls: d.cls||\'html\'\r\n                        }]\r\n                    });\r\n                    bindAceEditor();\r\n                });\r\n            });\r\n            SCRM.link(optsData, {\r\n                selHTML: \'Startup\'\r\n            });\r\n        } else {\r\n            bindAceEditor();\r\n        }\r\n    },\r\n    optsMain: function(runOpts) {\r\n        SCRM.link(optsData, {\r\n            optsTmpl: \'#tpl_optsMain\'\r\n        });\r\n        \r\n        pJSON(SCRM.files_url, {\r\n            uid: \'logoClub\',\r\n            thumb: 1\r\n        }, function(logodata){\r\n            $(\'#logoClub\').trigger(\'logoClub\', logodata);\r\n        });\r\n    },\r\n    optsPays: function(runOpts) {\r\n        getConfigFields(runOpts.paym, function(fields, opts) {\r\n            SCRM.link(optsData, {\r\n                payOpts: runOpts,\r\n                paym: opts,\r\n                optsTmpl: \'#tpl_optsPays\'\r\n            });\r\n            bindAceEditor();\r\n        });\r\n    }\r\n});\r\n\r\nSCRM.getPayMethods = function(parent, ptype) {\r\n    ptype = ptype || \'idCity_Pay\';\r\n    pDATA(\'idStatus\', {\r\n        row