(38, 'document', 'text/html', 'schedule', '', '', 'schedule', 1, 'data-cls=\"clubStuff\"', 1, 0, 0, 27, 0, NULL, '[[$tplClubFrameTop?bs=`1` &fa=`1`]]\r\n\r\n<div id=\"schedIframe\" data-mode=\"[[!#REQUEST.mode:default=`city`]]\" data-key=\"[[!#REQUEST.key:default=`1`]]\"></div>\r\n[[$schedInner]]\r\n\r\n<script>\r\n$(function() {\r\n    $(\'#schedIframe\').clubSchedule();\r\n});\r\n</script>\r\n\r\n</body>\r\n</html>', 0, 0, 7, 0, 1, 1, 1668777824, 0, 0, 0, 0, 0, 1521235860, 0, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'handlers/schedule.html', 0, 0, 1, NULL, 64),
