(41, 'document', 'text/html', 'ЛК SportCRM', '', '', 'sportcrm', 1, 'data-cls=\"clubStuff\"', 1, 0, 0, 21, 0, NULL, '<div id=\"scrmlk\"></div>\r\n\r\n<script>\r\nremoveObjectFromLocalStorage(\'saasData\');\r\n\r\n$(\'#scrmlk\').load(\'https://w.sportcrm.club/hook/scrmh/lk\', {\r\n    scrmlk: \'html\',\r\n    key: \'[[!++club_key]]\',\r\n    user: SCRM.app.user\r\n}, function(){\r\n    $(document)\r\n    .on(\'show.bs.modal\', \'#cmSdSCRM\',function(e) {\r\n        var mdl = $(e.target);\r\n            //v = $.view(mdl, true);\r\n        $(\'#SdSCRMlabel\', mdl).text(\'Пожелания и предложения\');\r\n    });\r\n});\r\n</script>', 0, 2, 1, 1, 1, 1, 1668777824, 0, 0, 0, 0, 0, 1556117340, 0, '', 0, 0, 0, 0, 1, 'modDocument', 'web', 1, 'options/sportcrm.html', 0, 0, 1, NULL, 150);
