(81, 'document', 'text/html', 'Новости', '', '', 'news', 1, 'data-cls=\"clubStuff\"', 1, 0, 0, 0, 0, '', '<script id=\"tmpl_player\" type=\"text/x-jsrender\">\r\n    {{if type==\"header\"}}\r\n        <h{{:data.level}} {{if tunes.anchorTune != undefined}} id=\"{{:tunes.anchorTune.anchor}}\" {{/if}}>{{:data.text}}</h{{:data.level}}>\r\n    {{else type==\"paragraph\"}}\r\n        <p style=\"text-align:{{:data.alignment}}\">{{:data.text}}</p>\r\n        \r\n    {{else type==\"image\"}}\r\n    <p class=\"text-center\">\r\n        <img src=\"{{:data.file.url}}\" class=\"img-fluid\">\r\n        <h5 class=\"text-muted text-center\">{{:data.caption}}</h5>\r\n    </p>\r\n    \r\n    {{else type==\"list\"}}\r\n        {{if data.style==\"ordered\"}}\r\n            <ol>\r\n            {{props data.items}}\r\n                <li>{{>prop}}</li>\r\n            {{/props}}\r\n            </ol>\r\n        {{else}}\r\n            <ul>\r\n            {{props data.items}}\r\n                <li>{{>prop}}</li>\r\n            {{/props}}\r\n            </ul>\r\n        {{/if}}\r\n        \r\n    {{else type==\"embed\"}}\r\n        <div class=\"embed-responsive embed-responsive-16by9 m-b-md mb-3\">\r\n            <iframe class=\"embed-responsive-item\" src=\"{{:data.embed}}\" width=\"560\" height=\"315\" frameborder=\"0\" allow=\"autoplay; encrypted-media; picture-in-picture\" allowfullscreen=\"\"></iframe>\r\n            <h5 class=\"text-muted text-center\">{{:data.caption}}</h5>\r\n        </div>\r\n        \r\n    {{else type==\"quote\"}}\r\n        <blockquote class=\"blockquote text-right\">\r\n            <p class=\"mb-0\">{{:data.text}}</p>\r\n            <footer class=\"blockquote-footer\">{{:data.caption}}</footer>\r\n        </blockquote>\r\n        \r\n    {{else type==\"raw\"}}\r\n        {{:data.html}}\r\n        \r\n    {{else type==\"ace\"}}\r\n        <div class=\"mb-4\">\r\n        	<div class=\"\" id=\"{{:id}}\"></div>\r\n        	<!-- <div class=\"card-footer mb-3 mt-1\">\r\n                <button class=\"btn btn-outline-secondary btn-xs btn-ace-save\" data-link=\"{on \'click\' ~copyAceCode}\">Скопировать</button>\r\n            </div> -->\r\n        </div>\r\n    {{else type==\"delimiter\"}}\r\n        <div class=\"log-divider\">\r\n          <span class=\"h3 mt-n2\">***</span>\r\n        </div>\r\n    {{else type==\"alert\"}}\r\n        <div class=\"alert alert-{{:data.type}}\" role=\"alert\">\r\n            {{:data.message}}\r\n        </div>\r\n    {{else type == \"table\"}}\r\n        <table class=\"table table-bordered\">\r\n        {{if data.withHeadings==true}}\r\n            {{for data.content end=1 }}\r\n                <thead>\r\n                    <tr>\r\n                        {{props #data}}\r\n                            <th scope=\"col\">{{:prop}}</th>\r\n                        {{/props}}\r\n                    </tr>\r\n                </thead>\r\n            {{/for}}\r\n            {{for data.content start=1}}\r\n                <tbody>\r\n                    <tr>\r\n                        {{props #data}}\r\n                            <td scope=\"col\">{{:prop}}</td>\r\n                        {{/props}}\r\n                    </tr>\r\n                </tbody>\r\n            {{/for}}\r\n            \r\n        {{else}}\r\n            {{for data.content}}\r\n                <tbody>\r\n                    <tr>\r\n                        {{props #data}}\r\n                            <td scope=\"col\">{{:prop}}</td>\r\n                        {{/props}}\r\n                    </tr>\r\n                </tbody>\r\n            {{/for}}\r\n        {{/if}}\r\n        </table>\r\n    {{else type == \"carousel\"}}\r\n        <div class=\"row\">\r\n            {^{for data}}\r\n                <div class=\"col-xl-3 col-lg-4 col-sm-4 shop-item pb-3\">\r\n                    <div class=\"card card-figure h-100 m-0\">\r\n                        <figure class=\"figure h-100 d-flex\">\r\n                            <div class=\"figure-img text-center\">\r\n                                <img class=\"img-fluid\" src=\"{{:url}}\" alt=\"\" id=\"img_shop{{:id}}\">\r\n                            </div>\r\n                            <figcaption class=\"figure-caption flex-grow-1\">\r\n                                <h6 class=\"figure-title\">\r\n                                    {{:caption}}\r\n                                </h6>\r\n                            </figcaption>\r\n                        </figure>\r\n                    </div>\r\n                </div>\r\n                \r\n                <!--<div class=\"col-6\">\r\n                    <p class=\"text-center\">\r\n                        <img src=\"{{:url}}\" class=\"img-fluid\">\r\n                        <h5 class=\"text-muted text-center\">{{:caption}}</h5>\r\n                    </p>\r\n                </div>-->\r\n            {{/for}}\r\n        </div>\r\n    {{/if}}\r\n</script>\r\n\r\n<script id=\"tpl_spBlockNotes\" type=\"text/x-jsrender\">\r\n<div class=\"card newscard\">\r\n    <div class=\"card-header d-flex text-muted small\"><div class=\"newsCreated\">{{formatDateTime:created}}</div></div>\r\n    <div class=\"card-body\">\r\n        <div data-link=\"{for extended^blocks tmpl=\'#tmpl_player\'}\"></div>\r\n        <div class=\"spinner-border text-primary\" style=\"width: 3rem; height: 3rem;\" role=\"status\" data-link=\"visible{:!extended^blocks.length}\">\r\n            <span class=\"sr-only\">Loading...</span>\r\n        </div>\r\n    </div>\r\n</div>\r\n</script>\r\n\r\n<div id=\"allnews\" data-link=\"{for notes tmpl=\'#tpl_spBlockNotes\' sort=\'created\' reverse=true}\"></div>\r\n\r\n<script>\r\nvar noteTab = {};\r\n\r\n$.link(true, \"#allnews\", noteTab);\r\n\r\npJSON(\"/hook/getnotes\", {}, function(notes){\r\n    $.each(notes.rows, function(k,v){\r\n        v.extended = v.extended;\r\n    })\r\n    SCRM.link(noteTab, {notes:notes.rows});\r\n});\r\n</script>', 0, 2, 1, 1, 1, 1, 1668802862, 1, 1668924975, 0, 0, 0, 1668802860, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'news.html', 0, 0, 1, NULL, 666),
(82, 'document', 'text/html', 'Создание новостей', '', '', 'addnews', 1, '', 1, 0, 0, 37, 0, '', '<div id=\"addNew\" class=\"ml-auto\">\r\n    <a href=\"#\" class=\"count-info nav-link nav-item\" data-link=\"{on create}\">\r\n        <i class=\"fa fa-plus\" aria-hidden=\"true\"></i>\r\n    </a>  \r\n</div>\r\n\r\n<script id=\"tmpl_create\" type=\"text/x-jsrender\">\r\n<div class=\"card\">\r\n    <div class=\"card-body\">\r\n        {^{include noteTab}}\r\n            <div id=\"editorjs\"></div>\r\n            <div class=\"spinner-border text-primary\" style=\"width: 3rem; height: 3rem;\" role=\"status\" data-link=\"visible{:!extended^blocks.length}\">\r\n                <span class=\"sr-only\">Loading...</span>\r\n            </div>\r\n        {{/include}}\r\n    </div>\r\n</div>\r\n</script>\r\n\r\n\r\n<script>\r\n    SCRM.wssRes[\'editorJs\'] = [\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/editorjs@latest/dist/editor.js\",\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/header@latest/dist/bundle.min.js\",\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/image@latest/dist/bundle.min.js\",\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/list@latest/dist/bundle.min.js\",\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/quote@latest/dist/bundle.min.js\",\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/embed@latest/dist/bundle.min.js\",\r\n        \"https://cdn.jsdelivr.net/npm/editorjs-paragraph-with-alignment@latest/dist/bundle.min.js\",\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/raw@latest/dist/bundle.min.js\",\r\n        \"/assets/clubtools/js/editorjs.js\",\r\n        \"https://cdn.jsdelivr.net/npm/editorjs-alert@latest/dist/bundle.min.js\",\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/delimiter@latest/dist/bundle.min.js\",\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/marker@latest/dist/bundle.min.js\",\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/table@latest/dist/bundle.min.js\",\r\n        \"https://cdn.jsdelivr.net/npm/@editorjs/inline-code@latest/dist/bundle.min.js\",\r\n    ];\r\n\r\n    $(\"#addNew\").appendTo(\'.page-navs\');\r\n    \r\n    var btns = `<button class=\"btn btn-icon btn-light ml-auto\"><i class=\"fa fa-pencil\"></i></button>\r\n    <button class=\"btn btn-icon btn-light\"><i class=\"fa fa-trash\"></i></button>`;\r\n    $(\".newscard .card-header\").append( $(btns) );\r\n    $(\".newsCreated\").addClass(\"pt-2\");\r\n    \r\n    var addNewData = {\r\n        create: function(e,d){\r\n            e.preventDefault();\r\n            var newsmdl = {\r\n                cm_size: \'lg\',\r\n                title: \"Создать новость\",\r\n                body: \'#tmpl_create\'\r\n            };\r\n            \r\n            club_Drawer(newsmdl)\r\n            .one(\'shown.bs.modal\', function(){\r\n                var noteTab = {\r\n                    id: \'tab_spBlockNotes\',\r\n                    tpl: \"#tpl_spBlockNotes\",\r\n                    aceIsLoad: false,\r\n                    extended:{\r\n                        blocks: []\r\n                    }\r\n                },\r\n                extendedTmpl = {\r\n                    time: Date.now(),\r\n                    blocks: [\r\n                        {\r\n                            id: \"extendedTmpl_h3\",\r\n                            type: \"header\",\r\n                            data: {\r\n                                text: \"Заголовок\",\r\n                                level: 3\r\n                            },\r\n                            tunes: {\r\n                                anchorTune: []\r\n                            }\r\n                        }\r\n                    ],\r\n                    version: \"2.24.3\"\r\n                },\r\n                saveByAjax = function(file) {\r\n                    var formData = new FormData(),\r\n                    params = {\r\n                        oper: \"add\",\r\n                        filename: file.name,\r\n                        uid: \"newsImage\",\r\n                        thumb: true,\r\n                        filetype: \"image\",\r\n                    };\r\n                    \r\n                    $.each(params, function(key, value){\r\n                        if ($.type(value) !== \'function\' && value !== null ) formData.append(key, value);\r\n                    });\r\n                    formData.append(0, file);\r\n                    \r\n                    return $.ajax({\r\n                        type: \"POST\",\r\n                		url: \'/hook/files\',\r\n                		contentType: false,\r\n                		processData: false,\r\n                		data: formData\r\n                    }).then(function(data){\r\n                        data = JSON.parse(data);\r\n                        if (data.error != \"\") {\r\n                            return {\r\n                                success: 0,\r\n                            }\r\n                        } else {\r\n                            return {\r\n                                success: 1,\r\n                                file: {\r\n                                    url: data.rows[data.rows.length-1].md,\r\n                                    key: data.rows[data.rows.length-1].key\r\n                                }\r\n                            }\r\n                        }\r\n                    })\r\n                }, //ф-ция сохранения картинок в карту спортсмена\r\n                renderAce = function(){\r\n                    $.each(noteTab.extended.blocks, function(key,value){\r\n                        if(value.type == \"ace\"){\r\n                            SCRM.loadWSS([\'ace\'], function(){\r\n                                var codeEditor = ace.edit(value.id),\r\n                                lang = value.data.language;\r\n                                codeEditor.setTheme(\"ace/theme/chrome\");\r\n                                codeEditor.session.setMode(\"ace/mode/\"+lang);\r\n                                codeEditor.setOptions({\r\n                                    maxLines: Infinity,\r\n                                    useWorker: false,\r\n                                    wrap: true\r\n                                });\r\n                                codeEditor.setValue(value.data.code);\r\n                                codeEditor.setReadOnly(true);\r\n                            })\r\n                        }\r\n                    });\r\n                }, //рендер ace в режиме просмотра\r\n                saveNote = function(extended){\r\n                    var newhash = JSON.stringify(extended.blocks).hashCode();\r\n                    if (newhash != noteTab.hash){\r\n                        var params = {\r\n                            extype: \'idSportsmen_notes\',\r\n                            extended: JSON.stringify(extended)\r\n                        };\r\n                        \r\n                        !noteTab.extid? params.oper = \"add\": params.id = noteTab.extid;\r\n                        \r\n                        pEDIT(\'idExtid\', params, function(add_data){\r\n            		        SCRM.link(noteTab).setProperty({\r\n                                extended: extended,\r\n                                hash: newhash,\r\n                                extid: add_data.id\r\n                            });\r\n                            //SCRM.success(\"Сохранено\");\r\n                        })\r\n                    }\r\n                }; //сохранение в extId\r\n                String.prototype.hashCode = function() {\r\n                    var hash = 0, i, chr;\r\n                    if (this.length === 0) return hash;\r\n                    for (i = 0; i < this.length; i++) {\r\n                        chr   = this.charCodeAt(i);\r\n                        hash  = ((hash << 5) - hash) + chr;\r\n                        hash |= 0; // Convert to 32bit integer\r\n                    }\r\n                    return hash;\r\n                }; //получения хэша строки для проверки изменилось ли содержимое заметки\r\n                $.observe(noteTab, \"extended\", function(e,d){//При изменении содержимого заметки, обновляется код в ace\r\n                    setTimeout(function(){\r\n                        renderAce();\r\n                    }, 500);\r\n                });\r\n                \r\n                SCRM.link(newsmdl, {noteTab: noteTab});\r\n                \r\n                if (!SCRM.editor) {//Если редактор еще не загружен\r\n                    console.log(\'!SCRM.editor\')\r\n                    SCRM.loadWSS([\'editorJs\'], function(){\r\n                        const editor = new EditorJS({\r\n                        	autofocus: true,\r\n                        	tools: {\r\n                        		header: {\r\n                                    class: Header,\r\n                                    tunes: [\'anchorTune\']\r\n                                },\r\n                        		image: {\r\n                                    class: ImageTool,\r\n                                    config: {\r\n                                        uploader: {\r\n                                            uploadByFile(file){\r\n                                                return saveByAjax(file);\r\n                                            },\r\n                                            \r\n                                            uploadByUrl(url){\r\n                                                return saveByAjax(file);\r\n                                            }\r\n                                        },\r\n                                    }\r\n                                },\r\n              	                list: {\r\n                                    class: List,\r\n                                    inlineToolbar: true,\r\n                        		},\r\n                        		quote: Quote,\r\n                        		embed: {\r\n                        			class: Embed,\r\n                        			config: {\r\n                        				services: {\r\n                        					youtube: true,\r\n                        					coub: true\r\n                        				}\r\n                        			}\r\n                        		},\r\n                        		paragraph: {\r\n                        			class: Paragraph,\r\n                        			inlineToolbar: true,\r\n                        	    },\r\n                        	    ace: Aceblock,\r\n                        	    raw: RawTool,\r\n                                alert: Alert,\r\n                                Marker: {\r\n                                    class: Marker,\r\n                                    shortcut: \'CMD+SHIFT+M\',\r\n                                },\r\n                                delimiter: Delimiter,\r\n                                table: {\r\n                                    class: Table,\r\n                                    inlineToolbar: true,\r\n                                    config: {\r\n                                        rows: 2,\r\n                                        cols: 3,\r\n                                    },\r\n                                },\r\n                                inlineCode: {\r\n                                    class: InlineCode,\r\n                                    shortcut: \'CMD+SHIFT+C\',\r\n                                },\r\n                                carousel: {\r\n                                    class: Carousel,\r\n                                    config: {\r\n                                        uploader: {\r\n                                            uploadByFile(file){\r\n                                                return saveByAjax(file);\r\n                                            },\r\n                                            \r\n                                            uploadByUrl(url){\r\n                                                return saveByAjax(file);\r\n                                            }\r\n                                        },\r\n                                    }\r\n                                },\r\n                                anchorTune: AnchorBlockTune\r\n                        	},\r\n                        	onReady: function(){\r\n                        	    SCRM.editor = editor;\r\n                                SCRM.link(noteTab).setProperty(\"mode\", \"show\");\r\n                                SCRM.link(noteTab).setProperty({\r\n                                    extended: extendedTmpl,\r\n                                    hash: false,\r\n                                    extid: false\r\n                                });\r\n                                SCRM.editor.blocks.render(extendedTmpl);\r\n                            },\r\n                            onChange: function(api, e){\r\n                                var block = noteTab.extended.blocks[e.detail.index];\r\n                                if (e.type == \"block-removed\" && e.detail.target.name == \"image\") {//Если удален блок image, удалить соответсвующий файл \r\n                                    pJSON(\"/hook/files\", {\r\n                                        oper: \"del\",\r\n                                        fkey: block.data.file.key,\r\n                                        uid: \"newsImage\"\r\n                                    }, function(data){\r\n                                    });\r\n                                } else if (e.type == \"block-removed\" && e.detail.target.name == \"carousel\") {//Если удален блок carousel, удалить все файлы которые в нем были \r\n                                    $.each(block.data, function(k,v){\r\n                                        var fkey = v.url.split(\"/\");\r\n                                        fkey = fkey[fkey.length-1].split(\".\");\r\n                                        fkey = fkey[0]+\".\"+fkey[1];\r\n                                        pJSON(\"/hook/files\", {\r\n                                            oper: \"del\",\r\n                                            fkey: fkey,\r\n                                            uid: \"newsImage\"\r\n                                        }, function(data){\r\n                                        });\r\n                                    });\r\n                                } else if (e.type == \"block-changed\" && e.detail.target.name == \"carousel\") {//Если изменен блок image, сравнить кол-во изображениий в нем до и после, если было удалено, то удалить и из файлов\r\n                                    e.detail.target.save().then(function(newBlock){\r\n                                        var old_keys = $.map(block.data, function(v){\r\n                                            var fkey = v.url.split(\"/\");\r\n                                            fkey = fkey[fkey.length-1].split(\".\");\r\n                                            fkey = fkey[0]+\".\"+fkey[1];\r\n                                            return fkey;\r\n                                        })\r\n                                        \r\n                                        var new_keys = $.map(newBlock.data, function(v){\r\n                                            var fkey = v.url.split(\"/\");\r\n                                            fkey = fkey[fkey.length-1].split(\".\");\r\n                                            fkey = fkey[0]+\".\"+fkey[1];\r\n                                            return fkey;\r\n                                        })\r\n                                        \r\n                                        var diff = $(old_keys).not(new_keys).get();\r\n                                        \r\n                                        $.each(diff, function(k, v){\r\n                                            pJSON(\"/hook/files\", {\r\n                                                oper: \"del\",\r\n                                                fkey: v,\r\n                                                uid: \"newsImage\"\r\n                                            }, function(data){\r\n                                            }); \r\n                                        })\r\n                                    });\r\n                                }\r\n                                //Таймер что бы не сохранять в бд заметку чаще чем раз в 2 сек\r\n                                if (SCRM.changeTimer) clearTimeout(SCRM.changeTimer);\r\n                                let changeTimer = SCRM.changeTimer = setTimeout(function(){\r\n                                    SCRM.editor.save().then(function(outputData){\r\n                                        saveNote(outputData);\r\n                                    });\r\n                                }, 2000);\r\n                            }\r\n                        });\r\n                    })\r\n                } else {\r\n                    SCRM.editor.blocks.clear();\r\n                    SCRM.link(noteTab).setProperty({\r\n                        extended: extendedTmpl,\r\n                        hash: false,\r\n                        extid: false\r\n                    });\r\n                    SCRM.editor.blocks.render(extendedTmpl);\r\n                }\r\n            });\r\n        }\r\n    };\r\n    \r\n    $.link(true, \"#addNew\", addNewData);\r\n</script>', 0, 0, 2, 0, 1, 1, 1668819107, 1, 1668840167, 0, 0, 0, 1668819240, 1, '', 0, 0, 0, 0, 1, 'modDocument', 'web', 1, 'handlers/modules/addnews.html', 0, 0, 1, NULL, 0),
(83, 'document', 'text/html', 'Камера', '', '', 'camera', 1, 'data-cls=\"clubStuff\"', 1, 0, 0, 0, 0, '', ' <style>\r\n video::-webkit-media-controls {\r\n  display: none;\r\n }\r\n  .camera{\r\n      display: flex;\r\n      align-content: center;\r\n      justify-content: center;\r\n      align-items: center;\r\n  }\r\n\r\n</style>\r\n\r\n\r\n\r\n <body onload=\"init();\">\r\n     <div class=\"m-2\">\r\n    <h3>Вы можете отсканировать элетромобиль и проверить его по нашей базе</h3>\r\n     <p><br>\r\n    <input type=\'button\' onclick=\"startWebcam();\" class=\"btn btn-primary\"  value=\"Сделать фото\"><br><br>\r\n    <div class=\"custom-file\">\r\n    <input type=\"file\" class=\"custom-file-input\" id=\"imageFiles\" multiple=\"\"><label class=\"custom-file-label\" for=\"tf3\"><ya-tr-span data-index=\"181-0\" data-translated=\"false\" data-source-lang=\"en\" data-target-lang=\"ru\" data-value=\"Choose file\" data-translation=\"Выберите фото\" data-ch=\"0\" data-type=\"trSpan\">Выберите файл</ya-tr-span></ya-tr-span></label>\r\n    </div><br><br>\r\n	<input type=\"button\" id=\"upload\" name=\"upload\" onclick=\"btnClick()\" value=\"Проверить авто\" class=\"btn btn-primary\">\r\n    </p>\r\n    <video onclick=\"snapshot(this);\"  autoplay=\"autoplay\" width=400 height=400 id=\"video\" controls autoplay></video>\r\n <!--<button onclick=\"snapshot();\">Take Snapshot</button>-->\r\n  <p>\r\n            Для того чтобы сделать фото нажмите на камеру\r\n            <br>\r\n            Ваше фото : <p>\r\n            <div class=\"camera\">\r\n      <canvas  id=\"myCanvas\" width=\"300%\" height=\"300%\"></canvas>\r\n      </div>\r\n      <img id=\"img12\"></img>\r\n      </div>\r\n  </body>\r\n  <script>\r\n      //--------------------\r\n      // GET USER MEDIA CODE\r\n      //--------------------\r\n          navigator.getUserMedia = ( navigator.getUserMedia ||\r\n                             navigator.webkitGetUserMedia ||\r\n                             navigator.mozGetUserMedia ||\r\n                             navigator.msGetUserMedia);\r\n      var video;\r\n      var webcamStream;\r\n      function startWebcam() {\r\n          \r\n        if (navigator.getUserMedia) {\r\n           navigator.getUserMedia (\r\n              // constraints\r\n              {\r\n                 video: { facingMode:   \"environment\" },\r\n                 audio: false,\r\n                  \r\n              },\r\n              // successCallback\r\n              function(localMediaStream) {\r\n                  video = document.querySelector(\'video\');\r\n                 video.srcObject=localMediaStream;\r\n                 webcamStream = localMediaStream;\r\n              },\r\n              // errorCallback\r\n              function(err) {\r\n                 console.log(\"The following error occured: \" + err);\r\n              }\r\n           );\r\n        } else {\r\n           console.log(\"getUserMedia not supported\");\r\n        }\r\n      }\r\n      function stopWebcam() {\r\n          webcamStream.stop();\r\n      }\r\n      //---------------------\r\n      // TAKE A SNAPSHOT CODE\r\n      //---------------------\r\n      var canvas, ctx;\r\n      function init() {\r\n        // Get the canvas and obtain a context for\r\n        // drawing in it\r\n        canvas = document.getElementById(\"myCanvas\");\r\n        ctx = canvas.getContext(\'2d\');\r\n      }\r\n      function snapshot() {\r\n         // Draws current image from the video element into the canvas\r\n        ctx.drawImage(video, 0,0, canvas.width, canvas.height);\r\n        \r\n        const canvas12 = document.getElementById(\'myCanvas\');\r\n        const img12    = canvas12.toDataURL(\'image/jpg\');\r\n        btnClickText(img12);\r\n        \r\n      }\r\n      \r\n  \r\n      function btnClick() {\r\n		let file_data = $(\'#imageFiles\').prop(\'files\')[0];   \r\n		let form_data = new FormData();                \r\n		form_data.append(\'file\', file_data);\r\n\r\n		$.ajax({\r\n			url: \'https://static.mvk.ru/hut/index.php\',\r\n			dataType: \'text\',\r\n			cache: false,\r\n			contentType: false,\r\n			processData: false,\r\n			data: form_data,\r\n			type: \'post\',\r\n			success: function(data){\r\n			    if (data != \"Номер не распознан\"){\r\n			        console.log(data);\r\n				    check_number(data);\r\n			    }\r\n			    else{\r\n			        alert(data);\r\n			    }\r\n			    \r\n			}\r\n		});\r\n	}\r\n	function btnClickText(img_data) {\r\n		let form_data = new FormData();                \r\n		form_data.append(\'textimg\', img_data);\r\n\r\n		$.ajax({\r\n			url: \'https://static.mvk.ru/hut/ind.php\',\r\n			dataType: \'text\',\r\n			cache: false,\r\n			contentType: false,\r\n			processData: false,\r\n			data: form_data,\r\n			type: \'post\',\r\n			success: function(data){\r\n			    if (data != \"Номер не распознан\"){\r\n			        console.log(data);\r\n				    check_number(data);\r\n			    }\r\n			    else{\r\n			        alert(data);\r\n			    }\r\n			}\r\n		});\r\n	}\r\n	function check_number(datas) {\r\n		$.ajax({\r\n			url: \'https://go.start123.ru/hook/api?gosnum=\'+datas,\r\n			dataType: \'json\',\r\n			type: \'GET\',\r\n			success: function(data){\r\n			 console.log(data);\r\n			 if (data.error){\r\n			     alert(\"Номер \" + datas +\" не заргистрирована в базе электромобилей\");\r\n			 }\r\n			 else if (data.status == 0 || data.status == 1){\r\n			      alert(\"Номер \" + datas +\" не проверен\");\r\n			 }\r\n			 else if (data.status == 2){\r\n			      SCRM.success(\"Номер \" + datas +\" принадлежит электромобилю\");\r\n			 }\r\n			 else if (data.status == 3){\r\n			     alert(\"Номер \" + datas +\" не электромобиль\");\r\n			 }\r\n			  else {\r\n			     alert(\"Номер \" + datas +\" не заргистрирована в базе электромобилей\");\r\n			 }\r\n			}\r\n		});\r\n	}\r\n	\r\n  </script>\r\n  </body> ', 0, 2, 8, 0, 1, 3, 1668838627, 3, 1668930883, 0, 0, 0, 1668838620, 3, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'camera.html', 0, 0, 1, NULL, 0);
