(84, 'document', 'text/html', 'Реестр транспорта', '', '', 'cardb', 1, 'data-cls=\"clubStuff\"', 1, 0, 0, 17, 0, '', '<div class=\"section-block my-0\">\r\n    <table id=\"grid_idCar\" data-entity=\"idCar\"></table>\r\n</div>\r\n\r\n<script>\r\n    SCRM.loadWSS([\'grid\'], function () {\r\n        grids.idTrainer = $(\"#grid_idCar\")\r\n        .jqGridInit({\r\n            sortname: \'status\', sortorder: \'asc\',\r\n            search: true,\r\n            colModel:[\r\n                {name: \'id\', hidden: true, template: idFieldTemplate},\r\n                {name: \'status\', label:\'Статус\', width:80,\r\n                    template: selTemplate,\r\n                    formatter: function(cv, options, rowObject) {\r\n                        return \'<a href=\"#\" data-lead=\"\'+ rowObject.id +\'\" class=\"prevent-default\">\'+ SCRM.statusName(\'idLead\', cv, null, true) +\'</a>\';\r\n                    },\r\n                    unformat: function(cellValue, options, cell) {\r\n                        return $(cell).find(\'i\').data(\'status\');\r\n                    },\r\n                    clubStatus: \'idCar\', lookupKey: \'alias\'\r\n                },\r\n                {name: \'gosnum\', label: \'Гос номер\', width: 200,\r\n                    editable: true,  setROW: true\r\n                },\r\n                {name: \'vin\', label: \'VIN\', width: 200,\r\n                    editable: true,  setROW: true\r\n                },\r\n                {name: \'owner\', label: \'Владелец\', width: 200,\r\n                    editable: true,  setROW: true\r\n                },\r\n                {name: \'idsportsmen\', label: \'Пользователь\', width: 200,\r\n                    editable: false,  setROW: true\r\n                },\r\n                {template: createdTemplate, hidden: false, label: \'Создано\'},\r\n            ],\r\n            navOpts: {add:true, edit:true, del:false},\r\n            fltrToolbar: true\r\n        })\r\n        .jqGridColumns()\r\n        .jqGridExport();\r\n    })\r\n</script>', 0, 2, 4, 0, 1, 1, 1668847427, 1, 1668942299, 0, 0, 0, 1668847380, 1, 'Реестр транспорта', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'database/cardb.html', 0, 0, 1, NULL, 0),
(85, 'document', 'text/html', 'Авто водителя', '', '', 'spcars', 1, '', 1, 0, 0, 37, 0, '', '<script id=\"idCars_tmpl\" type=\"text/x-jsrender\">\r\n<div class=\'card-body pt-0\'>\r\n<button type=\"button\" class=\"btn btn-primary\" data-link=\"{on addCar}\">Добавить авто</button>\r\n    <div class=\'row\'>\r\n        {^{for cars}}\r\n            <div class=\'col-sm-12 col-md-6 col-xl-4 my-2\'>\r\n                <div class=\"card card-figure h-100 m-0\">\r\n                  <figure class=\"figure h-100 d-flex\">\r\n                    <div class=\"figure-img text-center\">\r\n                      <img class=\"img-fluid\" src=\"{{:url}}\" alt=\"\">\r\n                    </div>\r\n                    <figcaption class=\"figure-caption flex-grow-1 d-flex\">\r\n                        <div>{{:vin}}</div>\r\n                            {{if status == 1}}\r\n                                <div class=\"tile tile-circle ml-auto\">\r\n                                    <span class=\"fa fa-clock-o\"></span> \r\n                                </div>\r\n                            {{else status == 2}}\r\n                                <div class=\"tile tile-circle ml-auto bg-success\">\r\n                                    <span class=\"fa fa-check\"></span> \r\n                                </div>\r\n                            {{else status == 3}}\r\n                                <div class=\"tile tile-circle ml-auto bg-danger\">\r\n                                    <span class=\"fa fa-times\"></span> \r\n                                </div>\r\n                            {{/if}}\r\n                        <div class=\"ml-auto\">{{:gosnum}}</div>\r\n                    </figcaption>\r\n                  </figure>\r\n                </div>\r\n            </div>\r\n        {{/for}}\r\n    </div>\r\n</div>\r\n</script>\r\n\r\n<script>\r\nvar tabCars = {\r\n    id: \"tabCars\",\r\n    tpl: \"#idCars_tmpl\",\r\n    name: \"Мои авто\",\r\n    addCar: function(){\r\n        var mdl = {\r\n            cm_size: \"lg\",\r\n            title: \"Добавить автомобиль\",\r\n            body: \"<div id=\'newtsform\'></div>\"\r\n        };\r\n        \r\n        club_Drawer(mdl)\r\n        .on(\'shown.bs.modal\', function(){\r\n            SCRM._run(`/chunk/eform/newts`, {\r\n                container: \'#newtsform\'\r\n            });\r\n        });\r\n    }\r\n}\r\nif (typeof sp_data != \"undefined\") $.observable(sp_data.addMenu).insert(tabCars);\r\n\r\n$(\'a[href=\"#tabCars\"]\').on(\'shown.bs.tab\', function (e) {\r\n    var urls = [\"https://5koleso.ru/wp-content/uploads/2022/01/img_5769-1024x683.jpg\",\"https://cdnstatic.rg.ru/uploads/images/169/12/62/35_e43ba14c.jpg\",\"https://moscowteslaclub.ru/upload/iblock/aef/aef61a6442bf9c86a44dc8b1e6333762.jpg\"];\r\n    pJSON(\"/hook/myCars\", {\r\n        key: sp_data.key\r\n    }, function(autos){\r\n        $.each(autos.rows, function(k, v){\r\n            v.url = urls[k*sp_data.id];\r\n            v.gosnum =  v.gosnum.toUpperCase();\r\n        });\r\n        SCRM.link(tabCars, {cars:autos.rows});\r\n    })\r\n})\r\n</script>', 0, 0, 3, 0, 1, 1, 1668864602, 1, 1668935733, 0, 0, 0, 1668864600, 1, '', 0, 0, 0, 0, 1, 'modDocument', 'web', 1, 'handlers/modules/spcars.html', 0, 0, 1, NULL, 0),
(86, 'document', 'text/html', 'Заправки', '', '', 'maps', 1, 'data-cls=\"clubStuff\"', 1, 0, 0, 0, 0, '', '<style>\r\n    iframe{\r\n        width : 100%;\r\n    }\r\n</style>\r\n\r\n<iframe src=\"https://yandex.ru/map-widget/v1/?um=constructor%3A8851ca8b7dcd17baf5ad6bfdcc0a77dc74575fa8be1efa2da6643b600b20f81d&amp;source=constructor\" width=\"432\" height=\"620\" frameborder=\"0\"></iframe>', 0, 2, 7, 0, 1, 3, 1668883437, 1, 1668924742, 0, 0, 0, 1668883380, 3, '', 0, 0, 0, 0, 0, 'modDocument', 'web', 1, 'maps.html', 0, 0, 1, NULL, 0);
