(11, 0, 0, 'lgnResetPassChangePassTpl', '', 0, 3, 0, '[[!+logcp.error_message:notempty=`<p style=\"color: red;\">[[+logcp.error_message]]</p>`]]\n\n<form class=\"form inline\" action=\"[[~[[*id]]]]\" method=\"post\">\n    <input type=\"hidden\" name=\"nospam:blank\" value=\"\" />\n    <input type=\"hidden\" name=\"lp\" value=\"[[!+logcp.lp]]\"/>\n    <input type=\"hidden\" name=\"lu\" value=\"[[!+logcp.lu]]\"/>\n\n    <div class=\"ff\">\n        <label for=\"password_new\">[[!%login.password_new]]\n            <span class=\"error\">[[+logcp.error.password_new]]</span>\n        </label>\n        <input type=\"password\" name=\"password_new:required\" id=\"password_new\" value=\"[[+logcp.password_new]]\" />\n    </div>\n\n    <div class=\"ff\">\n        <label for=\"password_new_confirm\">[[!%login.password_new_confirm]]\n            <span class=\"error\">[[+logcp.error.password_new_confirm]]</span>\n        </label>\n        <input type=\"password\" name=\"password_new_confirm:required\" id=\"password_new_confirm\" value=\"[[+logcp.password_new_confirm]]\" />\n    </div>\n\n    <br class=\"clear\" />\n\n    <div class=\"form-buttons\">\n        <input type=\"submit\" name=\"logcp-submit\" value=\"[[!%login.change_password]]\" />\n    </div>\n</form>\n', 0, 'a:0:{}', 0, ''),
(12, 0, 0, 'lgnExpiredTpl', '', 0, 3, 0, '<p><strong>Password Reset Information</strong></p>\n<p>Your password has already been reset or the link expired. If you need to reset your password again, click <a href=\"#\">here</a>.</p>', 0, 'a:0:{}', 0, ''),
(13, 1, 0, 'idLogoutMenu4', '', 0, 5, 0, '<button class=\"btn-account\" type=\"button\" data-toggle=\"dropdown\"[[- data-display=\"static\" aria-haspopup=\"true\" aria-expanded=\"false\"]]>\n    <span class=\"d-block d-md-none\">\n        <i class=\"fa fa-user-circle-o\"></i>\n    </span>\n    <span class=\"account-summary d-none d-md-block\">\n        <span class=\"account-name\">[[!#SESSION.user_fullname]]</span>\n        <span class=\"account-description\">[[!#SESSION.club_cityname]]</span>\n    </span>\n</button>\n<!-- .dropdown-menu -->\n<div class=\"dropdown-menu dropdown-menu-right\">\n    <div class=\"dropdown-arrow\"></div>\n    <h6 class=\"dropdown-header d-md-none\" id=\"user_fullname\">[[!#SESSION.user_fullname]]</h6>\n[[#SESSION.club_manager:notempty=`\n    <a class=\"dropdown-item\" href=\"/database/db.html\" id=\"chCity\">\n        <i class=\"dropdown-icon fa fa-map-marker\"></i> [[#SESSION.club_cityname]]\n    </a>\n`]]\n    <a class=\"dropdown-item\" href=\"/login/profile.html\"><i class=\"dropdown-icon fa fa-id-card-o\"></i> Профиль</a>\n    <div class=\"dropdown-divider\"></div>\n    <a class=\"dropdown-item\" href=\"[[+logoutUrl]]\"><i class=\"dropdown-icon fa fa-sign-out\"></i> Выход</a>\n</div>\n<!-- /.dropdown-menu -->\n[[-#SESSION.club_manager:notempty=`\n<li class=\"dropdown\">\n    <a class=\"dropdown-toggle\" data-toggle=\"dropdown\" href=\"#\" id=\"chCity\" aria-expanded=\"false\">\n        <i class=\"fa fa-map-marker\"></i> \n        [[-#SESSION.club_cityname]]\n        <span class=\"caret\"></span>\n    </a>\n    \n    <ul class=\"dropdown-menu\">\n        <li><a href=\"/database/db.html\">База данных</a></li>\n    </ul>\n</li>\n`]]', 0, 'a:0:{}', 0, ''),
(14, 1, 0, 'idLoginMenu4', '', 0, 5, 0, '[[-<li><a href=\"/?action=login\" class=\"showLogin\"><i class=\"fa fa-sign-in\"></i> Войти</a></li>]]\n\n<a href=\"/?action=login\" class=\"btn-account showLogin\"><i class=\"fa fa-sign-in\"></i>&nbsp;Войти</a>', 0, 'a:0:{}', 0, ''),
(15, 1, 0, 'tplModal', '', 0, 1, 0, '<div class=\"modal inmodal1 fade\" id=\"tpl_clubModal\" tabindex=\"-1\" role=\"dialog\" aria-hidden=\"true\" aria-labelledby=\"rmodalLabel\" data-link=\"aria-labelledby{:mdl_id+\'label\'}\">\n    <div class=\"modal-dialog\" data-link=\"class{merge:cm_size toggle=\'modal-\'+cm_size}\">\n        <div class=\"modal-content spinner-parent\" data-link=\"disabled{:loading}\">\n            <div class=\"color-line mb-3\"></div>\n            <div class=\"modal-header pt-0\" data-link=\"visible{:title||!onOK}\">\n                <h5 class=\"modal-title\" data-link=\"html{:title} id{:mdl_id+\'label\'}\" id=\"rmodalLabel\"></h5>\n                <button type=\"button\" class=\"close\" data-dismiss=\"modal\" aria-label=\"Close\">\n                    <span aria-hidden=\"true\">&times;</span>\n                </button>\n            </div>\n            <div class=\"modal-body pt-0 pb-3\" data-link=\"{include tmpl=body}\"></div>\n            <div class=\"modal-footer pt-0\" data-link=\"visible{:onOK||cancel||mdl_footer}\">\n                <div class=\"modal-footer-add flex-grow-1\" data-link=\"{if mdl_footer tmpl=mdl_footer}\"></div>\n                <button type=\"button\" class=\"btn btn-light btn-modal-cancel\"\n                    data-dismiss=\"modal\"\n                    data-link=\"html{:cancel? cancel:\'Отмена\'} visible{:onOK||cancel} disabled{:loading}\"></button>\n                <button type=\"button\" class=\"btn btn-primary btn-modal-ok\"\n                    data-link=\"visible{:onOK} {on \'click\' ~on_OK} disabled{:loading||disabledOK}\">\n                    <span data-link=\"html{:ok||\'OK\'}\"></span>\n                    <span class=\"spinner-border spinner-border-sm\" role=\"status\" data-link=\"visible{:loading}\"></span>\n                </button>\n            </div>\n        </div>\n    </div>\n</div>\n\n<div class=\"modal modal-drawer fade\" id=\"tpl_clubDrawer\" tabindex=\"-1\" role=\"dialog\" aria-hidden=\"true\" aria-labelledby=\"rdrawerLabel\" data-link=\"aria-labelledby{:mdl_id+\'label\'}\">\n    <div class=\"modal-dialog modal-drawer-right\" role=\"document\" data-link=\"class{merge:cm_size toggle=\'modal-\'+cm_size}\">\n        <div class=\"modal-content\" data-link=\"disabled{:loading}\">\n            <div class=\"modal-header d-flex\">\n                <div id=\"rdrawerLabel\" class=\"modal-title h6 flex-grow-1\" data-link=\"{include ^tmpl=title} id{:mdl_id+\'label\'}\"></div>\n                <button class=\"close d-print-none\" data-dismiss=\"modal\" aria-label=\"Close\"><span aria-hidden=\"true\">&times;</span></button>\n            </div>\n            <div class=\"modal-body my-2\" data-link=\"{include tmpl=body}\"></div>\n        </div>\n    </div>\n</div>', 0, 'a:0:{}', 0, ''),
(16, 0, 0, 'jsFormatStatus', 'Chunk', 0, 1, 0, '<script>\nvar Lstatus = [[!clubStatus?tbl=`idLesson` &keyfield=`alias`]],\nfmtLstatus = function(status) {\n    return makeIco( (status)? Lstatus[status].ico : false, {\n        //add: \' data-toggle=\"tooltip\" title=\"\'+ Lstatus[status].name +\'\"\',\n        big: 1\n    });\n};\n$.views.converters({\n    fmtLstatus: fmtLstatus\n});\n</script>', 0, NULL, 0, ''),
(17, 0, 0, 'panelPeriod', 'Chunk', 0, 1, 0, '<script id=\"tpl_selectPeriod\" type=\"text/x-jsrender\">\n<div class=\"input-group input-group-alt\">\n    <div class=\"input-group-prepend\">\n    <select class=\"custom-select selectPeriod\" data-link=\"{:selPeriod:} data-period{:~sPeriod(selPeriod)}\">\n        <option value=\"\">...</option>\n        {^{props period sort=\"prop.menuindex\"}}\n            <option data-link=\"text{:prop.name} value{:key}\"></option>\n        {{/props}}\n    </select>\n    </div>\n</div>\n</script>\n\n\n<script>\n(function( $ ){\n\n$.fn.panelPeriod = function(params){\n    params = (typeof params === \'string\')? {key: params} : params||{};\n    var pp = this;\n\n    var periods = { },\n        today = new Date(),\n        m = today.getMonth(),\n        y = today.getFullYear();\n    for (i = 0; i < 24; i++) {\n        periods[i] = {\n            name: (m+1)+\' / \'+y,\n            d1d2: [new Date(y, m, 1), new Date(y, m+1, 0, 23, 59, 59)],\n            menuindex: i,\n            m: m, y: y\n        };\n        if (m==0) { m = 11; y--; } else { m--; } // year\n    }\n\n    pp.each(function(){\n        var ppData = $.extend({\n            period: periods,\n            //d1d2: periods[0].d1d2, // TODO: Удалить\n            selPeriod: \'0\'\n        }, params);\n        ppData.selectedDates = periods[ppData.selPeriod].d1d2;\n\n        if (ppData.key) ppData = key2obj(SCRM, ppData.key, ppData);\n\n        var el = $(this).data(\'ppData\', ppData);\n        var input = el.is(\'input\')? el : $(\'<input />\').appendTo(el);\n        var pnl = $(\'<div></div>\').insertBefore(input);\n\n        el.trigger(\'periodInit\', ppData);\n        \n        $.templates(\'#tpl_selectPeriod\')\n        .link(pnl, ppData, {\n            sPeriod: function(val, e) {\n                var calendar = el.data(\'calendar\');\n                if (val && calendar) {\n                    setTimeout(function(){\n                        calendar.setDate(ppData.period[val].d1d2, true);\n                    }, 8);\n                }\n                return val;\n            }\n        });\n        \n        $(\'.input-group\', pnl).append(input);\n\n        input.addClass(\'form-control\')\n        .on(\'onChangeDateEdit\', function(e, data) {\n            console.log(\'onChangeDateEdit\', data);\n            if (data.selectedDates.length>=2) {\n                el.trigger(\'periodChange\', data.d1d2);\n                SCRM.link(ppData, {selectedDates: data.selectedDates});\n            }\n        })\n        .on(\'onInitDateEdit\', function(e, data) {\n            el.data(\'calendar\', data.instance);\n            //calendar = data.instance;\n            var nd = {};\n            $.each(ppData.period, function(i, v) {\n                var mName = flatpickr.l10ns.default.months.longhand[v.m];\n                if (mName) nd[i+\'.name\'] = mName +\' \'+ v.y;\n            });\n            SCRM.link(ppData.period, nd);\n        })\n        .initDateEdit({\n            mode: \'range\',\n            defaultDate: ppData.selectedDates\n            //locale: { rangeSeparator: \';\' }\n        });\n    });\n	return pp;\n};\n\n})( jQuery );\n</script>\n', 0, NULL, 0, ''),
(18, 0, 0, 'htmlModal', 'Chunk', 0, 1, 0, '<script>\nSCRM._service[\'[[+rq]]\'] = function(mdl) {\n    var title = (mdl.elm)? mdl.elm.text() : \'\';\n    mdl = $.extend({\n        title: \'#HTMLBlock_title\',\n        body: \'#HTMLBlock_body\',\n        cm_size: \'lg\',\n        printHelpTopic: function(e) {\n            SCRM.loadWSS(\'printThis\', function(){\n                $(\'.modal-body\', mdl.mdl)\n                .printThis({\n                    pageTitle: \'SportCRM - \'+title,\n                    printContainer: true,\n                    printDelay: 500\n                });\n            });\n        }\n    }, mdl);\n\n    club_Drawer(mdl);\n};\n</script>\n\n<script id=\"HTMLBlock_title\" type=\"text/x-jsrender\">\n<button class=\"btn btn-subtle-primary rounded-pill btn-sm\" data-link=\"{on printHelpTopic}\">\n    <i class=\"fa fa-print\"></i> Напечатать\n</button>\n</script>\n\n<script id=\"HTMLBlock_body\" type=\"text/x-jsrender\">\n    [[+HTMLBlock]]\n</script>', 0, NULL, 0, '');
