                              .one(\'jqGridSerializeGridData\', function(e, pd) {\n                                    var gr = $(e.target);\n                                    //var oldPostData = $.extend({}, pd);\n                                    gr.one(\'jqGridAfterLoadComplete\', function(e, data){\n                                        gr\n                                        .jqGrid(\'setSelection\', d.id)\n                                        .jqGrid(\'editGridRow\', d.id);\n                                        SCRM.link(spData, {needReload: true});\n                                        //.jqGrid(\'setGridParam\', {postData: oldPostData})\n                                    });\n                                    return { _where: {id: d.id} };\n                                })\n                                //.jqGrid(\'showCol\', \'id\')\n                                .trigger(\'reloadGrid\');\n                            } else {\n                                spGrid\n                                .one(\'jqGridAddEditInitializeForm\', function(e, form) {\n                                    $(\'#name\', form).val( srchData.srchTxt );\n                                })\n                                .jqGrid(\'editGridRow\', \'new\');\n                            }\n                            srchData.mdl_hide();\n                        }\n                    });\n                }, // end newSportsmen\n                changeField: function(chfield, e){\n                    SCRM._run.call(e, \'/chunk/sp.changefield\', {\n                        chfield: chfield,\n                        postData: {\n                            id: spData.selRows.join(\',\')\n                        },\n                        callback: spData.clearReload\n                    });\n                },\n                addInvoices: function(e){\n                    SCRM._run(\'/chunk/newinvoice\', {\n                        modal: {\n                            title: \'Массовые начисления\',\n                            ok: \'Начислить\'\n                        },\n                        post: {\n                            addmulti: \'sportsmen\',\n                            sportsmen: spData.selRows.join(\',\')\n                        }\n                    });\n                },\n                changeSquad: function(e, data) {\n                    SCRM._run(\'/chunk/squadlist\', {\n                        post: {\n                            ismain: \'1\',\n                            sportsmen: spData.selRows.join(\',\')\n                        },\n                        callback: spData.clearReload\n                    });\n                    /*squadAdd({\n                        ismain: \'1\',\n                        sportsmen: spData.selRows.join(\',\')\n                    }, function(data) {\n                        spData.reloadGrid();\n                        // TODO: Удалить, потому что окно будет закрыто ?\n                        SCRM._runLoader(\'spCard\')();\n                        //clubSpCard.loadData();\n                    });*/\n                },\n                exportQRcard: function(e, data) {\n                    e.preventDefault();\n                    var lnk = $(e.target);\n                    var data = $.extend({ // Чтобы не изменить Post\n                        doc_type: \'docx\',\n                        _export: lnk.data(\'handler\'),\n                        tmpl: lnk.data(\'tmpl\'),\n                    }, spGrid.jqGrid(\'getGridParam\', \'postData\'), {\n                        _where: \'id=\' + spData.selRows.join(\',\'),\n                        filters: \'\',\n                        rows: 1001 // Безлимитно\n                    });\n                    clubPostForm(spGrid.jqGrid(\'getGridParam\', \'url\'), data);\n                },\n                exportContract: function(e, data){\n                    clubPostForm(\'/data/sportsmen\', {\n                        key: spData.sp.key,\n                        mode: \'export\'\n                    });\n                },\n                sendMessage: function(e, data){\n                    pDATA(\'idSportsmen\', {\n                        _where: {id: spData.selRows.join(\',\')},\n                        rows: 1001\n                    }, function(data){\n                        SCRM._service.sendMessage(data.rows);\n                    });\n                },\n                clipAdd: function(e) {\n                    getGridSelRows(spGrid, function(selIDs){\n                        var clipSportsmen = get_clipSportsmen();\n                        $.each(selIDs, function(i, v){\n                            var itemIndex = $.inArray(v, clipSportsmen);\n                            if (itemIndex == -1) clipSportsmen.push(v);\n                        });\n                        saveObjectInLocalStorage(\'club_clip_sportsmen\', clipSportsmen);\n                    });\n                },\n                clipRemove: function(e){\n                    getGridSelRows(spGrid, function(selIDs){\n                        var clipSportsmen = get_clipSportsmen();\n                        $.each(selIDs, function(i, v){\n                            var itemIndex = $.inArray(v, clipSportsmen);\n                            if (itemIndex > -1) clipSportsmen.splice(itemIndex, 1);\n                        });\n                        saveObjectInLocalStorage(\'club_clip_sportsmen\', clipSportsmen);\n                    });\n                },\n                clipClear: function(e){\n                    removeObjectFromLocalStorage(\'club_clip_sportsmen\');\n                    SCRM.msg(\"Буфер обмена пуст!\", \"Успешно\");\n                },\n                clipShow: function(e) {\n                    spGrid.setGridFilter(\'id\', get_clipSportsmen().join(\',\'));\n                }\n            });\n    \n            $.observe(spData, \'idSchedule_main\', function(e, data){\n                spGrid.trigger(\'reloadGrid\');\n            });\n        \n            function saldoCls(row) {\n                var cellCls = [\'saldoCell\'];\n                if (row.saldo*1 > 0) cellCls.push(\'text-success\'); else {\n                    if (row.saldo*1 < 0) cellCls.push(\'text-danger\',\'font-weight-bold\');\n                    if (row.price && row.price*1 != 0  && row.saldo*1 < 0-row.price*1) cellCls.push(\'rowWarn\');\n                }\n                return cellCls;\n            }\n            \n            [[-function residueCls(row){\n                var cellCls = [\'residueCell\'], diff = diffNow(row.duedate);\n                if (diff < 0 && (row.residue > 0 || row.invtype_amount == 0)) {\n                    cellCls.push(\'text-success\');\n                } else {\n                    cellCls.push(\'text-muted\');\n                }\n                return cellCls;\n            }]]\n            \n            var narc = {id: \'narc\', name: \'[Не в архиве]\'};\n            var gridOpts = spData.gridOpts;\n            gridOpts.colModel.push(\n                {name: \'id\', template: idFieldTemplate},\n                {name: \'created\', template: createdTemplate},\n                {name: \'status\', width: 50, align: \'center\',\n                    template: lookTemplate,\n                    cellattr: function(rowId, cellValue, rawObject, cm, rdata){\n                        return arr2clstring( saldoCls(rawObject) );\n                    },\n                    formatter: function(cv){\n                        return SCRM.statusFmt(\'idSportsmen\', cv, \'name\', true);\n                    },\n                    unformat: function(cellValue, options, cell) {\n                        return $(cell).find(\'i\').data(\'status\');\n                    },\n                    searchoptions: {\n                        defaultValue: \'narc\'\n                    },\n                    clubStatus: \'idSportsmen\', lookupKey: \'name\'\n                },\n                {name: \'name\', //index: \'idSportsmen.name\',\n                    width: 200, editable: true, setROW: true, // Может обновляться через fieldEDIT\n                    formoptions: {rowpos: 1, colpos: 1},\n                    editrules: {edithidden: true, required: true}\n                },\n                {name: \'gender\', index: \'idSportsmen.gender\',\n                    width: 40, hidden: true,\n                    editable: true,\n                    editoptions: {\n                        defaultValue: spData.Gender_main\n                    },\n                    formoptions: {rowpos: 1, colpos: 2},\n                    template: selTemplate,\n                    clubStatus: \'Gender\', lookupKey: \'alias\'\n                },\n                //{name:\'num\', width:50, hidden: true, sorttype:\'int\'},\n                {name: \'birth\', index: \'idSportsmen.birth\',\n                    editable: true, editrules: {edithidden:true},\n                    formoptions:{rowpos:2, colpos:1},\n                    template: dateTemplate,\n                    cellattr: cellEmptyColor\n                },\n                {name: \'doc\', width: 100, hidden:true,\n                    editable: true, editrules: {edithidden:true}, setROW: true,\n                    formoptions:{rowpos: 2, colpos: 2},\n                    cellattr: cellEmptyColor\n                },\n                {name:\'contract\', width:80,\n                    editable: true, editrules: {edithidden:true}, setROW: true,\n                    formoptions:{rowpos:3, colpos:1},\n                    cellattr: cellEmptyColor\n                },\n                {name:\'contractdate\',\n                    editable: true, editrules: {edithidden:true},\n                    formoptions:{rowpos:3, colpos:2},\n                    template: dateTemplate,\n                    cellattr: cellEmptyColor\n                },\n                {name:\'contact\', width:100,\n                    editable: true, editrules: {edithidden:true}, setROW: true,\n                    formoptions:{rowpos:4, colpos:1},\n                    cellattr: cellEmptyColor,\n                    inCard: true\n                },\n                {name:\'pasp\', hidden:true,\n                    editable: true, editrules: {edithidden:true},\n                    formoptions:{rowpos:4, colpos:2},\n                    template: \'booleanCheckboxFa\',\n                    inCard: true\n                },\n                {name:\'email\', hidden: true, template: emailTemplate,\n                    editable: true,\n                    editrules: {edithidden: true}, //, email: true\n                    formoptions: {rowpos: 5, colpos: 1}\n                },\n                {name:\'tel\', template: telTemplate,\n                    editable: true,\n                    editrules: {edithidden: true},\n                    formoptions:{rowpos: 5, colpos: 2},\n                    cellattr: cellEmptyColor\n                },\n                {name: \'price\',\n                    editable: true, editrules: {edithidden: true},\n                    formoptions:{rowpos: 7, colpos: 1},\n                    template: numberTemplate\n                },\n                {name: \'discount\', width: 45, align: \'right\',\n                    editable: true, editrules: {edithidden:true},\n                    formoptions: {rowpos:7, colpos:2},\n                    template: selTemplate,\n                    //dbvalues: dbValues.Discount,\n                    clubStatus: \'Discount\', lookupKey: \'name\',\n                    dbvalues0: \'\'\n                },      \n        \n                {name:\'saldo\',\n                    template: numberTemplate,\n                    cellattr: function(rowId, cellValue, rawObject, cm, rdata){\n                        return arr2clstring( saldoCls(rawObject) );\n                    }\n                },\n                // duedate\n                {name: \'meddate\',\n                    editable: true, editrules: {edithidden:true},\n                    formoptions:{rowpos: 9, colpos: 1},\n                    template: dateTemplate,\n                    cellattr: cellInsuranceColor\n                },\n                {name: \'insdate\',\n                    editable: true, editrules: {edithidden: true},\n                    formoptions:{rowpos: 9, colpos: 2},\n                    template: dateTemplate,\n                    cellattr: cellInsuranceColor\n                },\n                {name: \'razr\', width: 50,\n                    editable: true, editrules: {edithidden:true},\n                    formoptions:{rowpos:10, colpos:1},\n                    template: selTemplate,\n                    clubStatus: \'idSportsmenGrade\', lookupKey: \'name\',\n                    dbvalues0: \'\'\n                },\n        \n                {name:\'level_name\', index:\'idSquad.lvl\', width: 55,\n                    template: lookTemplate,\n                    dbvalues: \'idLevel\'\n                },\n                {name:\'squad_name\', index:\'idSquad.name\', width: 55},\n                {name:\'trainer_name\', index: \'idTrainer.name\', width: 100},\n                {name:\'club_name\', index:\'idSquad.club\', width: 85,\n                    template: lookTemplate,\n                    dbvalues: \'idClub\'\n                },\n                {name:\'sport_name\', index:\'idSquad.sport\', width: 100,\n                    template: lookTemplate,\n                    dbvalues: \'idSport\'\n                },\n                {name: \'squad\', hidden: true, hidedlg: true,\n                    searchrules: { integer: true },\n                    editable: true, editrules: {edithidden:true}, //, required:true, integer:true\n                    formoptions:{rowpos:11, colpos:1},\n                    edittype: \'custom\',\n                    editoptions:{\n                        custom_element: function(value, edopt) {\n                            var el = $(\'<select><option>...</option></select>\');\n                            SCRM._run(\'/chunk/squadlist\', function(html) {\n                                el.html(html).val(value||0);\n                            });\n                            return el;\n                        }\n                    }\n                },\n                {name:\'trainer\', editable: true,\n                    hidden:true, hidedlg: true, editrules: {edithidden: true},\n                    formoptions: {rowpos: 11, colpos: 2},\n                    template: selTemplate,\n                    dbvalues: \'idTrainer\', dbvalues0: 0\n                },\n                {name: \'info\', hidden: true,\n                    template: infoTemplate,\n                    editable: true, editrules: {edithidden:true}, setROW: true,\n                    formoptions: {rowpos:12, colpos:1},\n                    inCard: true\n                },\n                {name: \'height\', width:50, hidden: true,\n                    editable: true, editrules: {edithidden:true},\n                    formoptions:{rowpos:13, colpos:1},\n                    inCard: true\n                },\n                {name:\'weight\', width:50, hidden: true,\n                    editable: true, editrules: {edithidden:true},\n                    formoptions:{rowpos:13, colpos:2},\n                    inCard: true\n                },\n                {name:\'username\',\n                    width:30, editable: false,\n                    search: false,\n                    align: \'center\',\n                    formatter: hasUser\n                },\n                {name:\'key\', hidden: true, editable: false}\n            );\n\n            \n            if (gridOpts.showDuedate) {\n                gridOpts.colModel.splice(16, 0, {\n                    name:\'duedate\', width: 110, hidden: false,\n                    align: \'right\',\n                    searchoptions: {\n                        sopt: [\'eq\']\n                    },\n                    formatter: function(cv, options, row){\n                        var str = [];\n                        //row.debt_lesson = row.lesson_amount*1 - row.cnt_lesson*1;\n                        if (row.duedate) {\n                            //if (row.debt_lesson != 0) \n                            if (row.cnt_lesson > 0) str.push(row.cnt_lesson);\n                            str.push( str2date(row.duedate,\'d\') );\n                            // if (diff > -90 || row.name_typeinvoice || residue===0) // Если < 3 мес или idInvoice\n                        }\n                        return str.join(\' ~ \'); // если пустое, будет пустая строка\n                    },\n                    cellattr: function(rowId, cellValue, data, cm, row){\n                        if (data.duedate) {\n                            return arr2clstring([\'text-success\']);\n                        }\n                        /*var cellCls = []; //, diff = diffNow(row.duedate);\n                        if (data.duedate && (data.debt_lesson > 0 || data.lesson_amount == 0)) {\n                            cellCls.push(\'text-success\');\n                        } else {\n                            cellCls.push(\'text-muted\');\n                        }\n                        return arr2clstring(cellCls);*/\n                    }\n                });\n            }\n            \n            \n            if (gridOpts.ipcFields) {\n                function cellSaldoColor2(rowId, cellValue, rawObject, cm, rdata){\n                    return rawObject.tmp_cls;\n                }\n                var ipcFields = {\n                    invoiced: {\n                        label: \'Начислено\',\n                        width: 90,\n                        template: numberTemplate,\n                        formatter: function(cv, options, row) {\n                            if (options.rowId == \'\') return (row.invoiced)? row.invoiced : \'\'; // if footer\n                            return numberFormatter(cv);\n                        },\n                        cellattr: cellSaldoColor2,\n                        inCard: true\n                    },\n                    add_cnt: {\n                        label: \'Количество\',\n                        template: numberTemplate,\n                        cellattr: cellSaldoColor2,\n                        inCard: true\n                    },\n                    payed: {\n                        label: \'Оплачено\',\n                        width: 90,\n                        template: numberTemplate,\n                        cellattr: cellSaldoColor2,\n                        inCard: true\n                    },\n                    add_info: {\n                        template: infoTemplate,\n                        inCard: true\n                    }\n                };\n                $.each(gridOpts.ipcFields.split(\',\'), function(i, v){\n                    var fld = ipcFields[v];\n                    if (fld) {\n                        fld.name = v;\n                        gridOpts.colModel.push(fld);\n                    }\n                });\n                $.extend(gridOpts, {\n                    footerrow: true,\n                    userDataOnFooter: true,\n                    multiselect: false\n                });\n                spGrid.one(\'jqGridAfterInit\', function(e){\n                    var hideCols = \'doc,orderdate,meddate,insdate,pasp,razr,contact,tel\'.split(\',\');\n                    $(this)\n                    .jqGrid(\'hideCol\', hideCols)\n                    .jqGridExport();\n                });\n            }\n        \n            grids.main = spGrid\n            .one(\'jqGridBeforeInit\', function(e, grOpts) {\n                var editedRows,\n                gr = $(this)\n                .one(\'jqGridAfterInit\', function(e, grOpts){\n                    $.each(gr.jqGrid(\'getGridParam\', \'colModel\'), function(i, v) {\n                        if (!v.label) {\n                            var nlabel = SCRM.lexicon.idSportsmen[v.name];\n                            if (nlabel && nlabel != v.name) { // Есть в справочнике\n                                gr.jqGrid(\'setLabel\', v.name, nlabel);\n                                v.label = nlabel;\n                            }\n                        }\n                    });\n                    grOpts.reloadAlert.link(true, spData);\n                    spData.pnlSp.link(true, spData);\n                    \n                    gr\n                    .jqGridColumns()\n                    .data(\'init-filters\', gr.jqGrid(\'getGridParam\', \'postData\').filters);\n                })\n                .on(\'jqGridAddEditBeforeInitData\', function (e, form, ddd) { // Редактируется строка\n                    var id = gr.jqGrid(\'getGridParam\', \'selrow\');\n                    console.log(\'jqGridAddEditBeforeInitData\',  e, form, ddd, id, editedRows);\n                    if (id in editedRows) {\n                        SCRM.alert(\'Данные в строке неактуальны. Обновите таблицу\');\n                        return false;\n                    }\n                })\n                .on(\'jqGridBeforeRequest.spGrid\', function(e) {\n                    var sstatus = gr.data(\'start_status\');\n                    if (sstatus) {\n                        var col = gr.jqGrid(\'getColProp\', \'status\');\n                        col.searchoptions.defaultValue = (sstatus==\'*\')? \'\' : sstatus;\n                        gr.data(\'start_status\', null);\n                    }\n                })\n                .on(\'jqGridSerializeGridData.spGrid\', function(e, postData) {\n                    if (gridOpts.showDuedate) postData._stype = spData.idSchedule_main;\n                    if (postData.clubStatus) postData.clubStatus.push(\'idSchedule\');\n                })\n                .on(\'jqGridBeforeSetLookup\', function(e, params){\n                    if (params.f.name == \'status\') {\n                        params.rows.unshift(narc);\n                    }\n                })\n                .on(\'jqGridAfterLoadComplete.spGrid\', function(e, data) {\n                    editedRows = {};\n                    if (data.sp_arc) SCRM.app.sp_arc = data.sp_arc.split(\',\');\n                    if (data.dbvalues && data.dbvalues.idTrainer) {\n                        SCRM.dbValues.idTrainer = data.dbvalues.idTrainer;\n                    }\n                    SCRM.link(spData, {needReload: false});\n                })\n                .on(\'needReload.spGrid\', function(e, data) {\n                    SCRM.link(spData, {needReload: true});\n                });\n                \n                // При изменении данных в eCard\n                $(document)\n                .on(\'eCardSpEdit\', function(e, data){\n                    var id = \'\'+data.id,\n                        ids = gr.jqGrid(\'getDataIDs\');\n                    if (in_array(id, ids)) {\n                        editedRows[id] = true;\n                        SCRM.link(spData, {needReload: true});\n                    }\n                });\n                \n                // TODO: Выпилить, чтобы было однообразно\n                $(document)\n                .on(\'OnRefreshSpData\', function(e, data) {\n                    // Если при обновлении карточки меняется\n                    var id = (data.sp||{}).id,\n                        ids = gr.jqGrid(\'getDataIDs\');\n                    if (in_array(id, ids)) SCRM.link(spData, {needReload: true});\n                });\n            })\n            .jqGridInit(gridOpts)\n            .on(\'jqGridAddEditInitializeForm\', function(e, form, oper) {\n                var tr = $(\'tr#tr_info\', form);\n                $(\'td:eq(1)\', tr).attr(\'colspan\', 3); \n                $(\'td:gt(1)\', tr).remove();\n                \n                var gen = $(\'#gender\', form).css(\'width\', \'auto\');\n                $(\'select.customelement\', form).addClass( gen.attr(\'class\') );\n            })\n            .on(\'jqGridSelectRow jqGridSelectAll\', function(e, rid, sel) {\n                var grid = $(this);\n                //console.log(e, rid, sel)\n                var selRows = getGridSelRows(grid);\n                var nsd = {\n                    //loading: 1,\n                    spVisible: false,\n                    selRows: selRows,\n                    selRowsCnt: selRows.length // spCount\n                };\n        \n                if (selRows.length == 1) {\n                    nsd.spVisible = true;\n                    nsd.spID = selRows[0];\n                    nsd.sp = grid.jqGrid(\'getRowData\', nsd.spID);\n        \n                    var tr = grid.jqGrid(\'getGridRowById\', nsd.spID);\n                    var mainCols = grid.jqGrid(\'getGridParam\', \'colModel\');\n                    [[-nsd.infoTable = $.map(mainCols, function(col, i){\n                        if (!col.inCard) return null;\n                        var cdata = $.jgrid.getDataFieldOfCell.call(grid[0], tr, i);\n                        col.HTML = cdata.html();\n                        col.CLS = cdata.attr(\'class\');\n                        return col;\n                    });]]\n                    nsd.cardHolder = spData.pnlSp;\n                    //nsd.cardModal = true;\n                    SCRM._runLoader(\'spCard\')(nsd); // spVisible = true\n                    //clubSpCard.loadData(); \n                }\n                SCRM.link(spData, nsd);\n            })\n            .on(\'jqGridDeleteBeforeShowForm\', function (e, form) {\n                $(\'.delmsg\', form).html(\'Удаляются спортсмены только в статусе <b>\'+ \n                    SCRM.app.sp_arc.join(\', \') +\'</b>.<br>Продолжить?\');\n            })\n            .on(\'jqGridDeleteAfterComplete\', function(e, rowid, jqXhrOrBool, postData, options) {\n                $(this).trigger(\'clearSelection\');\n            });\n        });\n    } else {\n        if (initOpts && spGrid.hasClass(\'ui-jqgrid-btable\')) {\n            spGrid.jqGrid(\'setGridParam\', initOpts);\n        }\n        spGrid.trigger(\'reloadGrid\');\n    }\n});\n</script>\n\n[[-\ncolModel:\n        {name: \'act\', template: \'actions\', width: 38}\n    ,\n    actionsNavOptions: {\n        editbutton: false\n    }\n]]', 0, NULL, 0, '');
INSERT INTO `mx_site_htmlsnippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `static`, `static_file`) VALUES
(20, 0, 0, 'schedInner', 'Chunk', 0, 1, 0, '[[-<link rel=\"stylesheet\" type=\"text/css\" href=\"/assets/table/table.css\">]]\n\n<script>\n$.fn.clubSchedule = function (start_params) {\n  var clubSchedule = this;\n\n  var schData = $.extend({\n    params: {\n        url: \'/hook/schedule.data/\',\n        f: {}\n    },\n    postData: {\n        mode: clubSchedule.data(\'mode\'),\n        key: function() {\n            return schData.key || clubSchedule.data(\'key\');\n        },\n        cnt_squad: clubSchedule.data(\'cnt_squad\')\n    },\n    fltr_names: {\n      idStype: {\n        name: \'Тип\',\n        fields: {\n            stype:\'stype_name\'\n        }\n      },\n      idSport: {\n        name: \'Дисциплина\',\n        fields: {\n            sport:\'sportname\'\n        }\n      },\n      idClub:{\n        name: \'Зал\',\n        fields: {\n            club:\'clubname\'\n        }\n      } ,\n      idTrainer: {\n        name: \'Тренер\',\n        fields: {\n            trainer:\'trainername\',\n            trainer2:\'trainername2\'\n        }\n      },\n      idSquad: {\n        name: \'Группа\',\n        fields: {\n            squad: \'squadname\',\n        },\n        tpl: \'#tpl_ScheduleFltrSquad\',\n        buildFunc: function(schl, nd) {\n            if (schl.squadclubname) {\n                // Для фильтров - если больше одной группы\n                var fltrsSquad = key2obj(nd, \'fltr_names.idSquad.rows\', { })[schl.squad] = schl.squad;\n                \n                var fltrsSquadClub = key2obj(nd, \'fltr_names.idSquad.squadClub\', { });\n                var idSquadClub = key2obj(fltrsSquadClub, schl.squadclub, {\n                    name: schl.squadclubname,\n                    idSquad: { }\n                });\n                key2obj(idSquadClub.idSquad, schl.squad, schl);\n            }\n        }\n      }\n    },\n    buildSchData: function(rows, nd) {\n        nd = $.extend({times: {}, dates: {}, cnt: 0}, nd);\n        //console.log(\'nd\', nd)\n        $.each(rows, function(i, schl) {\n            for (var fltr in schData.fltr_names) {\n                var frow = schData.fltr_names[fltr];\n                if (frow.value) {\n                    var in_filter = false;\n                    if (frow.fltrFunc) {\n                        in_filter = frow.fltrFunc(frow, schl);\n                    } else for (var fld_name in frow.fields) {\n                        if (frow.value === schl[fld_name]) in_filter = true; //goto each\n                    }\n                    if (!in_filter) return true;\n                }\n            }\n            var ts = schl.d_s.split(\':\')[0];\n            var time = key2obj(nd.times, ts, {});\n            key2obj(time, schl.d_w).push(schl);\n            key2obj(nd.dates, schl.d_w, {});\n            nd.cnt++;\n        });\n        $.each((nd.serverData || schData.serverData).dates, function(i, d) {\n            // Имена дней\n            if (d.day in nd.dates) nd.dates[d.day] = d;\n        });\n        console.log(\'nd\', nd);\n        SCRM.link(schData, nd);\n        \n        clubSchedule.trigger(\'clubSchedule.onbuild\', schData);\n    }\n  }, start_params);\n\n  clubSchedule\n  .addClass(\'club-schedule\')\n  .on(\'click\', \'.btn-custom-arrow\', function(e){\n      schData.postData.weekstart = schData.serverData[ $(this).data(\'nav\') ]\n      clubSchedule.trigger(\'clubSchedule.reload\');\n  })\n  .data(\'schData\', schData)\n  .on(\'clubSchedule.reload\', function(event, reload_params) {\n    $.extend(true, schData, reload_params);\n    $.observable(schData).setProperty(\'loading\', true);\n\n    pJSON(schData.params.url, schData.postData, function(data){\n      var nd = {\n        loading: false,\n        weekname: data.weekname, //getWeekName(data),\n        serverData: data,\n        updated: new Date()\n      };\n      $.each(data.idSchedule, function(i, schl){\n        $.each(schData.fltr_names, function(k, frow) {\n            if (frow.buildFunc) {\n                frow.buildFunc(schl, nd);\n            } else $.each(frow.fields || {}, function(f_key, fname) {\n                // если обозначены поля, то будут обновлены справочники\n                var name = schl[fname];\n                if (name) {\n                    var fltr = key2obj(nd, \'fltr_names.\'+k+\'.rows\', {});\n                    key2obj(fltr, schl[f_key], {\n                        name: name\n                    });\n                }\n            });\n        });\n      });\n      // TODO: Проверить текущие фильтры и если их нет в новых данных - сбросить в пустую строку\n      schData.buildSchData(data.idSchedule, nd);\n      clubSchedule.trigger(\'clubSchedule.onload\', schData);\n    });\n  });\n\n    $.observe(schData.fltr_names, \'**\', function(e, data){\n        if (data.path==\'value\') {\n          schData.buildSchData(schData.serverData.idSchedule);\n        }\n    });\n\n    $.templates(\'#tpl_ScheduleWrapper\')\n    .link(clubSchedule, schData, {\n        showFilter: function(fltr) {\n            var fltr_names = {};\n            if (fltr) $.each(schData.fltr_names, function(i, v){\n                if (SCRM.countProps(v.rows||{}) > 1) fltr_names[i] = v;\n            });\n            return fltr_names;\n        }\n    });\n\n    $.views.converters({\n      [[-daynameslice: function(date) {\n        date = date.replace( /[ауеоя]/ig, \'\' );\n        return date.slice(0, 2);\n      },\n      dateslice: function(date) {\n        var a = date.split(\"-\");\n        if( a.length < 2) return date[0];\n        return [ a[2], a[1] ].join(\".\");\n      },]]\n      fioslice: function(fio) {\n        var a = fio.split(\' \');\n        a[1] = (a[1])? a[1].slice(0,1)+\'.\':\'\';\n        a[2] = (a[2])? a[2].slice(0,1)+\'.\':\'\';\n        return [ a[0], a[1]+a[2] ].join(\' \');\n      },\n      fioslicerev: function(fio) {\n        var a = fio.split(\' \');\n        return [ a[1]||\'\', a[2]||\'\', a[0] ].join(\' \');\n      }\n    });\n\n    return clubSchedule.trigger(\'clubSchedule.reload\');\n};\n</script>\n\n<script id=\"tpl_ScheduleWrapper\" type=\"text/x-jsrender\">\n<div class=\"top-block-schedule\">\n    <div data-link=\"{include tmpl=\'#tpl_ScheduleDates\'}\" class=\"dates-schedule mb-2\"></div>\n    <div data-link=\"{include tmpl=\'#tpl_ScheduleFltrs\'}\" class=\"row fltr-schedule hidden-print\"></div>\n</div>\n<div class=\"alert alert-warning\" data-link=\"visible{:cnt==0}\">Нет данных</div>\n<div class=\"table-responsive\" data-link=\"visible{:cnt>0}\">\n<table class=\"tbl-schedule table table-condensed table-hover m-0 w-auto\">\n    <thead>\n    <tr>\n    <td></td>\n    {^{props dates sort=\"prop.date\"}}\n        <td>\n        <div class=\"day-head\">{{:prop.datefmt}}\n            [[-<b>{{daynameslice:prop.dayname}}</b>,\n            {{dateslice:prop.date}}]]\n        </div>\n        </td>\n    {{/props}}\n    </tr>\n    </thead>\n    <tbody data-link=\"{props times sort=\'key\' ~dates=dates ~srvData=serverData tmpl=\'#tpl_ScheduleRow\'}\"></tbody>\n</table>\n</div>\n</script>\n\n<script id=\"tpl_ScheduleRow\" type=\"text/x-jsrender\">\n<tr>\n    <td>{{>key}}:00</td>\n    {{props ~dates sort=\"prop.date\" ~data_time=prop}}\n    <td class=\"days-td\"\n        data-link=\"{for ~data_time[prop.day] tmpl=\'#tpl_ScheduleItem\'}\"></td>\n    {{/props}}\n</tr>\n</script>\n\n<script id=\"tpl_ScheduleDates\" type=\"text/x-jsrender\">\n<button class=\"btn btn-outline-primary btn-sm btn-custom-arrow\" data-nav=\"weekthis\"\n    data-link=\"disabled{:loading} {on ~navClick}\">\n    <i class=\"fa fa-step-forward\"></i>\n</button>\n<button class=\"btn btn-outline-primary btn-sm btn-custom-arrow\" data-nav=\"weekprev\"\n    data-link=\"disabled{:loading} {on ~navClick}\">\n    <i class=\"fa fa-chevron-left\"></i>\n</button>\n<button class=\"btn btn-outline-primary btn-sm btn-custom-arrow\" data-nav=\"weeknext\"\n    data-link=\"disabled{:loading} {on ~navClick}\">\n    <i class=\"fa fa-chevron-right\"></i>\n</button>\n<b class=\"weekname\">{^{:(loading)? \'Loading...\':weekname}}</b>\n</script>\n\n<script id=\"tpl_ScheduleFltrs\" type=\"text/x-jsrender\">\n{^{props ~showFilter(updated) }} [[-fltrs,weekname)]]\n    <div class=\"col-6 col-lg-auto\">\n    <select data-link=\"prop.value\" class=\"custom-select\">\n        <option value=\"\">-- {{:prop.name}} --</option>\n        {{if prop.tpl tmpl=prop.tpl }}\n        {{else}}\n            {^{props prop.rows sort=\"prop.name\"}}\n            <option value=\"{{:key}}\">{{:prop.name}}</option>\n            {{/props}}\n        {{/if}}\n    </select>\n    </div>\n{{/props}}\n</script>\n\n<script id=\"tpl_ScheduleFltrSquad\" type=\"text/x-jsrender\">\n{^{props prop.squadClub sort=\"prop.name\"}}\n    <optgroup label=\"{{:prop.name}}\">\n        {{props prop.idSquad sort=\"prop.squadname\"}}\n        <option value=\"{{:key}}\">{{:prop.squadname}} ({{:prop.ssportname}})</option>\n        {{/props}}\n    </optgroup>\n{{/props}}\n</script>\n\n<script id=\"tpl_ScheduleItem\" type=\"text/x-jsrender\">\n<div class=\"sch-item\" data-id=\"{{:id}}\" data-parent=\"{{:parent}}\" data-planfact=\"{{:planfact}}\">\n    <div class=\"sch-header\">\n        <span class=\"sch-time\">\n            {{:d_s}} - {{:d_e}}\n            <i class=\"fa fa-check-circle-o text-muted\" data-link=\"visible{:planfact==1} title{formatDateTime:datestart}\" data-toggle=\"tooltip\"></i>\n        </span>\n        <span class=\"badge badge-subtle badge-primary\" data-toggle=\"tooltip\"\n            data-link=\"title{:sportname} css-background-color{:sportcolor}\">{{:ssportname}}</span>\n    </div>\n    <div class=\"sch-body\">\n      [[-<div class=\"sch-sportname\">{{:sportname}}</div>]]\n      <div class=\"sch-trainer\">{{fioslice:trainername}}</div>\n      <div class=\"sch-trainer\" data-link=\"visible{:trainername2}\">{{fioslice:trainername2}}</div>\n      <div class=\"sch-squadname\">{{:squadname}}\n        {{if squad!=\'0\' && ~srvData.cnt_squad && !~srvData.cnt_squad[squad]}}\n        <i class=\"fa fa-exclamation-triangle\" data-toggle=\"tooltip\" title=\"Пустая группа\"></i>\n        {{/if}}\n      </div>\n      <div class=\"sch-clubname\">{{:clubname}}{{if location}}, {{:location}}{{/if}}</div>\n    </div>\n</div>\n</script>\n\n<style>\n\n.club-schedule .sch-item {\n    display: block;\n    background-color: white;\n    border-radius: 3px;\n    min-width: 160px;\n    margin-bottom: 0.25rem;\n    padding: 0.8rem;\n    /*min-height: 160px;*/\n    position: relative;\n}\n\n.club-schedule .sch-item:last-child {\n  margin-bottom: 0;\n}\n\n.club-schedule  .print-schedule img {\n  width: 23px;\n  height: 32px;\n}\n\n.col-lg-auto {\n  width: 14.1%;\n}\n\n@media (min-width: 767.98px) {\n  .club-schedule .top-block-schedule {\n    padding-left: 4rem;\n  }\n}\n\n.club-schedule .btn-custom-arrow {\n  width: 32px;\n  height: 32px;\n  background: #F4F5F7;\n  border-radius: 8px;\n  border: none;\n  padding: 0 !important;\n  color: #515C74;\n}\n\n.club-schedule .btn-custom-arrow img,\n.club-schedule .btn-custom-arrow i {\n  margin-top: -2px;\n}\n\n.club-schedule .btn-custom-arrow:hover {\n  background: #EFF2F6;\n}\n\n.club-schedule .btn-custom-arrow:active {\n  background: #ECF0F5 !important;\n  color: #fff !important;\n  background-color: #ECF0F5 !important;\n  border-color: #ffffff !important;\n}\n\n.club-schedule  .btn-outline-primary:hover {\n  background-color: #EBEEF2 !important;\n  border-color: #EBEEF2 !important;\n  color: #515C74 !important;\n  font-weight: 500;\n}\n\n.club-schedule .btn-outline-primary.focus,\n.club-schedule .btn-outline-primary:focus {\n  box-shadow: none !important;\n}\n\n.club-schedule .dates-schedule {\n    margin-left: -3px;\n}\n\n@media (max-width: 767.98px) {\n  .fltr-schedule, .fltr-schedule .form-control,\n  .club-schedule .tbl-schedule,\n  .club-schedule .dates-schedule {\n    font-size: .9em;\n  }\n\n  .club-schedule .top-block-schedule {\n    padding-left: 1rem;\n  }\n\n  .club-schedule .btn-custom-arrow {\n    width: 42px;\n    height: 42px;\n  }\n\n  .club-schedule .weekname {\n    padding-top: 0;\n    font-size: 1.2rem;\n  }\n\n  .tbl-schedule .sch-trainer {\n    font-size: 0.875rem !important;\n  }\n\n  .club-schedule .days-td {\n    min-width: 15rem;\n  }\n}\n\n.tbl-schedule tr > td:first-child {\n  border: none;\n  padding-top: 1.65rem;\n  font-style: normal;\n  font-weight: normal;\n  font-size: 0.75rem;\n  line-height: 14px;\n  color: #515C74;\n  width: 3.5rem;\n  text-align: center;\n}\n\n.tbl-schedule tbody tr:hover,\n.tbl-schedule tbody tr:hover {\n  background-color: transparent;\n}\n\n.tbl-schedule tr:last-child > td:first-child {\n  border: none;\n}\n\n.row.fltr-schedule {\n  margin-right: 0px;\n}\n\n.tbl-schedule .day-head {\n    padding-left: 1.2rem;\n    color: #14142B;\n    min-width: 10rem;\n}\n\n.tbl-schedule .day-head small {\n    color: #515C74;\n}\n\n.tbl-schedule .sch-time,\n.tbl-schedule .sch-type {\n    font-weight: 500;\n    font-size: 0.75rem;\n    line-height: 14px;\n    color: #515C74;\n}\n\n.tbl-schedule .sch-body {\n    color: #14142B;\n}\n\n.tbl-schedule .sch-clubname {\n  line-height: 13px;\n  /*display: flex;\n  align-items: center;*/\n}\n\n.tbl-schedule .sch-trainer {\n  font-weight: bold;\n  line-height: 15px;\n}\n\n.tbl-schedule .sch-squadname {\n  line-height: 14px;\n}\n\n.tbl-schedule .sch-time,\n.tbl-schedule .sch-clubname {\n  color: #515C74;\n}\n\n/*.table-responsive {\n  height: calc(100vh - 96px);\n  overflow-y: scroll;\n}*/\n\n.club-schedule .sch-item > div,\n.club-schedule .sch-body > div{\n  padding-bottom: 8px;\n}\n\n.club-schedule .fltr-schedule {\n  padding-right: .5rem;\n}\n\n.club-schedule .fltr-schedule > div {\n  margin-bottom: .5rem;\n  padding: 0 0 0 .5rem;\n}\n\n.tbl-schedule {\n    /*overflow: hidden;*/\n}\n\n.tbl-schedule th, .tbl-schedule td {\n    padding: 8px;\n    vertical-align: top;\n}\n\n.tbl-schedule tbody td {\n    border-left: 1px solid #F4F5F7;\n    border-top: 1px solid #F4F5F7;\n}\n.tbl