(29, 0, 0, 'tplSearch', 'Chunk', 0, 1, 0, '<script id=\"tpl_searchinput\" type=\"text/x-jsrender\">\n<form class=\"input-group input-group-alt srch-group\" action=\"\" data-link=\"{on \'submit\' ~srchSubmit}\">\n    <input type=\"text\" class=\"srch-input form-control clubmodal-focus\" \n        data-link=\"{:srchTxt:} placeholder{:~placeholder}\" />\n    <div class=\"input-group-append\">\n        <button type=\"submit\" class=\"btn btn-secondary\"><i class=\"fa fa-search\"></i></button>\n    </div>\n</form>\n</script>\n\n<script id=\"tpl_rowSportsmen\" type=\"text/x-jsrender\">\n<a href=\"#\" class=\"list-group-item list-group-item-action list-group-item-primary\" data-link=\"{on ~chSportsmen}\">\n    <div class=\"list-group-item-body\">\n    <h4 class=\"list-group-item-title\">{{:name}}</h4>\n    <h5 class=\"list-group-item-subtitle\">{{formatDate:birth}}</h5>\n    </div>\n    <div class=\"list-group-item-figure\">\n        <i data-link=\"class{:\'fa \' + (~cls? ~cls:\'fa-plus\')}\"></i>\n    </div>\n</a>\n</script>', 0, NULL, 0, ''),
(30, 0, 0, 'tableInner', 'Chunk', 0, 1, 0, '<style>\n    .table-wo-border tr:first-child td,\n    .table-wo-border tr:first-child th {\n        border-top: 0;\n    }\n    b.lessonmark {display:none;}\n    .showmarks b.lessonmark {display:block;}\n    .showmarks i {display:none;}\n    @page {\n        size: landscape;\n    }\n</style>\n\n[[$panelPeriod]]\n\n<div id=\"tableTabs\" class=\"card\">\n    <div class=\"ml-auto top2Nav\">\n        <a href=\"javascript:window.print();\" class=\"btn btn-light btn-icon\"><i class=\"fa fa-print\"></i></a>\n    </div>\n    <div class=\"card-header\" id=\"tablePeriod\"></div>\n    <div class=\"tab-content card-body\">\n        <div id=\"tbl_counts\" class=\"tab-pane active table-responsive \"\n            data-link=\"{if sum tmpl=\'#tpl_counts\'}\">\n            Loading ...\n        </div>\n        <div id=\"tbl_byday\" class=\"tab-pane\" data-link=\"{if tbl_params tmpl=\'#tpl_byday\'}\"></div>\n        <div id=\"tbl_lessonevent\" class=\"tab-pane hidePanelPeriod\"></div>\n    </div>\n</div>\n\n<script id=\"tpl_counts\" type=\"text/x-jsrender\">\n<table class=\"table table-bordered1 table-hover m-0 w-auto\">\n<thead>\n    <tr>\n        <th class=\"px-0\">\n        <select class=\"form-control form-control-sm custom-select1 custom-select-sm1\" data-link=\"sum_type\">\n        {{props sum_types }}\n            <option value=\"{{>key}}\">{{:~root.rate_types[key]}}</option>\n        {{/props}}\n        </select>\n        </th>\n        <th>\n            <a href=\"#\" data-link=\"{on ~showDetails} {:rate_types[ sum_types[sum_type] ]}\"></a>\n        </th>\n    </tr>\n</thead>\n<tbody>\n    {^{props sum[sum_type] sort=\"prop.name\"}}\n    <tr>\n        <td>\n            <a href=\"#\" data-link=\"{on ~selRow \'\'}\" class=\"d-flex\">\n                <span class=\"mr-auto\">{{:prop.name}}</span>\n                <span>\n                    <span class=\"badge badge-primary ml-3\">{{>prop.cnt}}</span>\n                </span>\n            </a>\n        </td>\n        <td>\n            {^{props prop^rows sort=\"prop.name\" ~k=key}}\n                <a href=\"#\" class=\"d-flex\" data-link=\"{on ~selRow ~k} class{merge:#index>0 toggle=\'border-top pt-2 mt-2\'}\">\n                    <span class=\"mr-auto\">{{:prop.name}}</span>\n                    <span>\n                        <span class=\"badge badge-success ml-3\">{{>prop.cnt}}</span>\n                    </span>\n                </a>\n            {{else}}\n                <a href=\"#\" class=\"pull-right badge badge-success\" data-link=\"{on ~showDetails}\">{{:~S.countProps(prop.details)}}</a>\n            {{/props}}\n        </td>\n        [[-<td>{{>prop.cnt_y}}</td>\n        <td>{{>prop.cnt_lesson}}</td>]]\n    </tr>\n    {{/props}}\n</tbody>\n<tfoot>\n    <tr>\n    <td colspan=\"2\">Итого: <b data-link=\"sum^cnt\"></b></td>\n    </tr>\n</tfoot>\n</table>\n</script>\n\n<script id=\"tpl_byday\" type=\"text/x-jsrender\">\n<table class=\"table table-wo-border w-auto\">\n    <tbody>\n    {^{for tbl_params ~sqtype=sqtype}}\n        <tr>\n            <th data-id=\"{{:id}}\" data-key=\"{{:key}}\">{{:~root.rate_types[key]}}</th>\n            <td>\n                {{:name}}\n                <div data-link=\"{radiogroup ~sqtype} visible{:key==\'squad\'}\" class=\"mt-1\">\n                    <div class=\"custom-control custom-control-inline custom-radio\">\n                        <input type=\"radio\" class=\"custom-control-input\" name=\"sqtype\" id=\"sqtype1\" value=\"idSchedule.squad\">\n                        <label class=\"custom-control-label\" for=\"sqtype1\">По расписанию</label>\n                    </div>\n                    <div class=\"custom-control custom-control-inline custom-radio\">\n                        <input type=\"radio\" class=\"custom-control-input\" name=\"sqtype\" id=\"sqtype2\" value=\"idSportsmen.squad\">\n                        <label class=\"custom-control-label\" for=\"sqtype2\">По спортсменам</label>\n                    </div>\n                </div>\n            </td>\n        </tr>\n    {{/for}}\n    </tbody>\n</table>\n\n{^{if tdata==\'loading\' tmpl=\'Loading\'}}\n{{else ~td=tdata ~tdsp=tdata.sp ~dm=tdata.dm ~spfld=sp_field ~showMark=(ttype==\'mark\')}}\n<div class=\"table-responsive mb-3\">\n    <table class=\"table table-bordered table-sm table-hover text-center w-auto m-0\">\n        <thead>\n            <tr>\n                <td colspan=\"2\">\n                    <select class=\"custom-select custom-select-sm\" data-link=\"ttype\">\n                        <option value=\"lesson\">Посещаемость</option>\n                        <option value=\"mark\">Оценки</option>\n                    </select>\n                </td>\n                {{props ~dm sort=\"prop.datestart\"}}\n                    <th colspan=\"{{:prop.sch.length}}\">{{>key}}</th>\n                {{/props}}\n                <th colspan=\"2\" data-link=\"visible{:~showMark}\">Оценки</th>\n                <th rowspan=\"2\">{{fmtStatus:\'y\' tbl=\'idLesson\'}}</th>\n                <th rowspan=\"2\">Всего</th>\n            </tr>\n            <tr>\n                <th>N</th>\n                <th>Ф.И.О.</th>\n                {{props ~dm sort=\"prop.datestart\" }}\n                    {{for prop.sch sort=\"datestart\"}}\n                        <th class=\"small\">{{:ssportname}}<br>{{:d_s}}</th>\n                    {{/for}}\n                {{/props}}\n                <th data-link=\"visible{:~showMark}\">Сум</th>\n                <th data-link=\"visible{:~showMark}\">Ср</th>\n            </tr>\n        </thead>\n        <tbody>\n            {{props ~tdsp sort=\"prop.name\" itemVar=\"~sp\"}} \n            <tr>\n                <td class=\"text-right\" data-id=\"{{:key}}\">{{:#index+1}}</td>\n                <td class=\"text-left\">\n                    <b>{{:prop.name}}</b>\n                    <div data-link=\"{for ~spflds(~spfld, ~tdsp, key) tmpl=\'#tpl_spflds\'}\"></div>\n                </td>\n                \n                [[-{{props ~dm sort=\"prop.datestart\"}}\n                    {{for prop.sch sort=\"datestart\" tmpl=\"#tpl_tableCell\" /}}\n                {{/props}}]]\n                {^{for ~tableCells(prop.sch, ~showMark) sort=\"datestart\" tmpl=\"#tpl_tableCell\" /}}\n                \n                <td data-link=\"visible{:~showMark} {:~sp.prop.mark_sum||\'\'}\"></td>\n                <td data-link=\"visible{:~showMark} {:~sp.prop.mark_cnt? ~S.round2(~sp.prop.mark_sum/~sp.prop.mark_cnt):\'\'}\"></td>\n                <td>{{:~sp.prop.cnt_y}}</td>\n                <td>{{:~sp.prop.cnt}}</td>\n            </tr>\n            {{/props}}\n        </tbody>\n        <tfoot>\n            <tr>\n                <th class=\"text-right\" colspan=\"2\">{{fmtStatus:\'y\' tbl=\'idLesson\'}}</th>\n                {{props ~dm sort=\"prop.datestart\"}}\n                    {{for prop.sch sort=\"datestart\"}}\n                    <td>{{:~td[\'sch\'+schedule+\'y\']}}</td>\n                    {{/for}}\n                {{/props}}\n                <th rowspan=\"2\" colspan=\"2\" data-link=\"visible{:~showMark}\"></th>\n                <th class=\"bg-secondary\">{{:cnt_y}}</th>\n                <th></th>\n            </tr>\n            <tr>\n                <th class=\"text-right\" colspan=\"2\">Всего</th>\n                {{props ~dm sort=\"prop.datestart\"}}\n                    {{for prop.sch sort=\"datestart\"}}\n                    <td>{{:~td[\'sch_cnt\'+schedule]}}</td>\n                    {{/for}}\n                {{/props}}\n                <th></th>\n                <th class=\"bg-secondary\">{{:cnt}}</th>\n            </tr>\n        </tfoot>\n    </table>\n</div>\n    \n<div class=\"text-muted d-print-none\" data-link=\"{checkboxgroup sp_field}\">\n    {{props sp_fields}}\n        <div class=\"custom-control custom-control-inline custom-checkbox\">\n            <input type=\"checkbox\" class=\"custom-control-input\"\n                id=\"spf_{{:key}}\" name=\"spf_{{:key}}\" value=\"{{:key}}\">\n            <label class=\"custom-control-label\" for=\"spf_{{:key}}\">\n                {{:prop}}\n            </label>\n        </div>\n    {{/props}}\n</div>\n{{/if}}\n</script>\n\n<script id=\"tpl_tableCell\" type=\"text/x-jsrender\">\n    <td class=\"{{:cls}}\">{{:html}}</td>\n</script>\n\n<script id=\"tpl_spflds\" type=\"text/x-jsrender\">\n    <div class=\"mt-1 small\">\n        {{if field==\'status\'}}\n            {{:~S.statusName(\'idSportsmen\', value, \'name\', true)}}\n        {{else field==\'saldo\'}}\n            {{include sp tmpl=\'#tpl_spSaldo\' /}}\n        {{else field==\'email\'}}\n            <a href=\"mailto:{{:value}}\" target=\"_blank\">{{:value}}</a>\n        {{else field==\'tel\'}}\n            <a href=\"#\" class=\"telPopover\">{{:value}}</a>\n        {{else}}\n            {{:value}}\n        {{/if}}\n    </div>\n</script>\n\n[[$tpl_spSaldo]]\n\n<script>\n\nSCRM.link(SCRM.app, {\n    topNavTabs: [\n        {href: \'#tbl_counts\', html: \'Посещаемость\', active: true, hook: \'dates\'},\n        {href: \'#tbl_byday\', html: \'Табель\', hidden: true, hook: \'dates,tbl_params,sqtype\'},\n        {href: \'#tbl_lessonevent\', html: \'Результаты\', url: \'/chunk/lessoneventreport\'}\n    ]\n});\n\nSCRM.setClubStatus([[!clubStatus?tbl=`idLesson,idSportsmen`]]);\n\nSCRM.tableData = {\n    sum: {},\n    sum_type: \'trainer\',\n    sum_types: {\n        trainer: \'squad\',\n        squad: \'trainer\'\n    },\n    rate_type: \'squad\',\n    rate_types: {\n        squad: \'Зал, Группа\',\n        trainer: \'Тренер\'\n    },\n    sp: {},\n    sp_field: [],\n    sp_fields: {\n        status: \'Статус\',\n        contract: \'Договор\',\n        saldo: \'Баланс\',\n        tel: \'Телефон\',\n        email: \'E-mail\',\n        contact: \'Контакт\'\n    },\n    sqtype: \'idSchedule.squad\',\n    ttype: \'lesson\'\n};\n\n$(function(){\n    function _dateStart(v) {\n        var t = v.datestart.split(/[- :T]/);\n        v.dm = t[2]+\'.\'+t[1];\n        //v.dt = t[3]+\':\'+t[4];\n    }\n    \n    var tData = SCRM.tableData;\n    \n    $.link(true, \'#tableTabs\', tData, {\n        tableCells: function(sch, mode) {\n            //console.log(\'tableCells\', sch, mode)\n            var cells = [];\n            $.each(tData.tdata.dm, function(idm, dm) {\n                $.each(dm.sch, function(i, s) {\n                    var c = sch[s.schedule]||{\n                        datestart: s.datestart\n                    },\n                    cls = c.wo_invoice? [\'table-warning\'] : [];\n                    if (mode && c.mark && c.mark!=\'0\') {\n                        c.html = c.mark;\n                    } else if (c.status) {\n                        c.html = SCRM.statusFmt(\'idLesson\', c.status);\n                        cls.push(\'small\');\n                    }\n                    c.cls = cls.join(\' \');\n                    cells.push(c);\n                });\n            });\n            return cells;\n        },\n        spflds: function(spflds, tdsp, key) {\n            // Много параметров потому что спортсмены догружаются\n            //console.log(\'spflds\', spflds, tdsp, key);\n            var sp = tdsp[key];\n            return $.map(spflds, function(v, i){\n                if (sp[v]) return {field: v, value: sp[v], sp: sp};\n            });\n            \n        },\n        selRow: function(parent, e, data){\n            e.preventDefault();\n\n            var d = data.linkCtx.data;\n            var tbl_params = [{\n                key: (parent)? tData.sum_types[tData.sum_type] : tData.sum_type,\n                id: d.key,\n                name: d.prop.name\n            }];\n            \n            if (parent) {\n                tbl_params.unshift({\n                    key: tData.sum_type,\n                    id: parent,\n                    name: tData.sum[tData.sum_type][parent].name\n                });\n            }\n            SCRM.link(tData, {tbl_params: tbl_params});\n            $(\'[href=\"#tbl_byday\"]\').tab(\'show\');\n            \n            /*var tab = $(\'a[href=\"#tbl_byday\"]\').view();*/\n        },\n        showDetails: function(e, data) {\n            e.preventDefault();\n            var d = data.linkCtx.data;\n            var key = tData.sum_type, detail_key = tData.sum_types[key];\n            $.each(tData.sum[key], function(i, v){\n                if (!d.key || d.key == i) {\n                    $.each(v.details, function(n, drow) {\n                        drow.name = tData.sum[detail_key][n].name;\n                    });\n                    SCRM.link(v, {rows: v.details});\n                }\n            });\n        }\n    });\n\n    $(document)\n    .on(\'calculate\', \'[href=\"#tbl_counts\"]\', function(e) {\n        var tbl = spinnr(\'#tbl_counts\');\n        pDATA(\'idSchedule\', $.extend({\n            rows: 1001,\n            _report: \'idSchedule_table\'\n        }, tData.dates), function(data){\n            var sum = {\n                trainer: {},\n                squad: {},\n                cnt_y: 0, \n                cnt_lesson: 0,\n                cnt: 0\n            };\n            \n            function default_obj(name) {\n                return {\n                    name: name,\n                    details: {},\n                    cnt: 0\n                };\n            }\n\n            $.each(data.rows, function(i, v) {\n                var sq_name = (v.squad==\'0\')? \'-\' : v.clubname+\'<br>\' + joinStr([v.levelname, v.squadname]);\n                var row_sq = key2obj(sum.squad, v.squad, default_obj(sq_name));\n                row_sq.cnt += 1;\n                \n                var row_tr = key2obj(sum.trainer, v.trainer, default_obj(v.trainer_name));\n                row_tr.cnt += 1;\n                key2obj(row_tr.details, v.squad, {cnt: 0}).cnt += 1;\n                key2obj(row_sq.details, v.trainer, {cnt: 0}).cnt += 1;\n                \n                if (v.trainer2 != \'0\' && v.trainer2 != v.trainer) {\n                    row_tr = key2obj(sum.trainer, v.trainer2, default_obj(v.trainer2_name));\n                    row_tr.cnt += 1;\n                    key2obj(row_tr.details, v.squad, {cnt: 0}).cnt += 1;\n                    key2obj(row_sq.details, v.trainer2, {cnt: 0}).cnt += 1;\n                }\n                \n                sum.cnt += 1;\n                sum.cnt_y += v.cnt_y*1;\n                sum.cnt_lesson += v.cnt_lesson*1;\n            });\n            //console.log(sum);\n            SCRM.link(tData, {sum: sum});\n            spinnr(false, tbl);\n        });\n    })\n    .on(\'calculate\', \'[href=\"#tbl_byday\"]\', function(e) {\n        SCRM.link(tData, {tdata: \'loading\'});\n        \n        var _where = {};\n        $.each(tData.tbl_params, function(i, param) {\n            var key = (param.key==\'squad\')? tData.sqtype : \'idSchedule.\'+ param.key;\n            _where[key] = param.id;\n        });\n        \n        pDATA(\'idLesson\', $.extend({\n            rows: 1001,\n            _report: \'idLesson_table\',\n            _where: _where\n        }, tData.dates), function(data){\n            var tdata = {dm: {}, sp: {}, cnt: 0};\n            $.each(data.rows, function(i, v){\n                if (!v.schedule) v.schedule = v.schedule_id;\n                \n                if (v.status) {\n                    var status_key = \'cnt_\'+v.status;\n                    tdata[\'sch\'+v.schedule+v.status] = (tdata[\'sch\'+v.schedule+v.status] || 0) + 1;\n                    tdata[\'sch_cnt\'+v.schedule] = (tdata[\'sch_cnt\'+v.schedule] || 0) + 1;\n    \n                    var sp = key2obj(tdata.sp, v.sportsmen, {name: v.sportsmen_name, sch:{}, cnt: 0, mark_sum: 0, mark_cnt: 0});\n                    v.wo_invoice = ((v.cfg_value||\'\').indexOf(v.status) > -1) && !v.dateinvoice;\n                    if (v.mark) v.mark *= 1;\n                    sp.mark_sum += v.mark;\n                    if (v.mark) sp.mark_cnt++;\n\n                    sp.sch[v.schedule] = v;\n                    sp.cnt += 1;\n                    sp[status_key] = (sp[status_key] || 0) + 1;\n\n                    tdata.cnt += 1;\n                    tdata[status_key] = (tdata[status_key] || 0) + 1;\n                }\n                \n                _dateStart(v);\n                var dm = key2obj(tdata.dm, v.dm, {datestart: v.datestart, sch:[], sch_keys:{}});\n                if (!dm.sch_keys[v.schedule]) {\n                    dm.sch_keys[v.schedule] = true;\n                    dm.sch.push(v);\n                }\n            });\n            \n            SCRM.link(tData, {tdata: tdata});\n            //console.log(tdata)\n            /*if (_where[\'idSportsmen.squad\']) {\n                _where[\'idSchedule.squad\'] = _where[\'idSportsmen.squad\'];\n                delete _where[\'idSportsmen.squad\'];\n            }\n            // Если на занятии не было людей, то не будет записи в idLesson\n            pDATA(\'idSchedule\', $.extend({\n                rows: 1001,\n                _report: \'idSchedule_table\',\n                _where: _where\n            }, tData.dates), function(data){\n                $.each(data.rows, function(i, v){\n                    _dateStart(v);\n                    key2obj(tdata.dm, v.dm, {datestart: v.datestart, sch:[]}).sch.push(v);\n                });\n                SCRM.link(tData, {tdata: tdata});\n                //console.log(\'idSchedule_table\', tdata);\n            });*/\n                \n            \n            var sp_keys = array_keys(tdata.sp);\n            if (sp_keys.length > 0) pDATA(\'idSportsmen\', {\n                rows: 1001,\n                _where: {id: sp_keys}\n            }, function(data) {\n                var nspd = {};\n                $.each(data.rows, function(i, v) {\n                    //nspd[v.id] = $.extend({}, v, tdata.sp[v.id]); //\'tdata.sp.\'+\n                    nspd[v.id] = $.extend({}, v, tdata.sp[v.id]);\n                });\n                console.log(nspd)\n                SCRM.link(tData.tdata, {sp: nspd});\n            });\n        });\n    });\n    \n    $.observe(tData, \'dates\', \'tbl_params\', \'sqtype\', function(e, data){\n        $.each(SCRM.app.topNavTabs, function(i, v){\n            var hook = (v.hook||\'\').split(\',\'),\n                el = $(\'a[href=\"\'+ v.href +\'\"]\');\n            if (el.length && ($.inArray(data.path, hook) >= 0)) {\n                el.reCalculate();\n            }\n        });\n    });\n\n    var tablePeriod = $(\'#tablePeriod\')\n    .on(\'periodChange\', function(e, dates){\n        SCRM.link(tData, {dates: dates});\n    })\n    .panelPeriod();\n    \n    $(\'#topNavTabs\')\n    .on(\'shown.bs.tab\', \'[data-toggle=\"tab\"]\', function(e) {\n        var href = $(this).attr(\'href\');\n        tablePeriod.toggle( !$(href).hasClass(\'hidePanelPeriod\') ); \n    });\n\n    $(\'#tableTabs .top2Nav\').appendTo(\'header.page-navs\');\n});\n</script>', 0, NULL, 0, ''),
(31, 0, 0, 'tplInvoiceLesson', 'Chunk', 0, 1, 0, '<script id=\"tpl_InvoiceLesson\" type=\"text/x-jsrender\">\n<a href=\"#\" id=\"cntLesson{{:id}}\" data-link=\"visible{:InvoiceLesson} data-spkey{:key}\" data-run=\"/chunk/invoice.lesson\">\n    {^{if InvoiceLesson.length}}\n        <i class=\"fa fa-battery-3 mr-1\"></i>\n        {^{:(rest_lesson > 0)? rest_lesson+\' ~ \' : \'\'}}\n        {^{formatDate:duedate}}\n    {{else}}\n        <i class=\"fa fa-battery-0 text-warning animated flash infinite\" data-toggle=\"tooltip\" title=\"Без абонемента\"></i>\n    {{/if}}\n</a>\n[[-\n<div class=\"dropdown\" data-link=\"visible{:InvoiceLesson}\">\n    <a href=\"#\" id=\"dropdownCntLesson{{:id}}\"\n        data-toggle=\"dropdown\" aria-haspopup=\"true\" aria-expanded=\"false\">\n        {^{if InvoiceLesson.length}}\n            <i class=\"fa fa-battery-3 mr-1\"></i>\n            {^{:(rest_lesson > 0)? rest_lesson+\' ~ \' : \'\'}}\n            {^{formatDate:duedate}}\n        {{else}}\n            <i class=\"fa fa-battery-0 text-warning animated flash infinite\" data-toggle=\"tooltip\" title=\"Без абонемента\"></i>\n        {{/if}}\n    </a>\n    <div class=\"dropdown-menu\" aria-labelledby=\"dropdownCntLesson{{:id}}\" style=\"z-index:1001\">\n        {^{for InvoiceLesson}}\n        <div data-link=\"class{merge:(#index>0) toggle=\'mt-1 pt-1 border-top\'}\" class=\"mx-2\">\n        {{:invoice_name}}<br>\n        <span class=\"text-muted\">{{:(lesson_amount-cnt_lesson)}} ~ {{formatDate:duedate}}</span></div>\n        {{/for}}\n        {{if ~addMenu}}\n            <div class=\"dropdown-divider\" data-link=\"visible{:InvoiceLesson.length}\"></div>\n            {{include tmpl=~addMenu /}}\n        {{/if}}\n    </div>\n</div>]]\n</script>', 0, NULL, 0, ''),
(32, 0, 0, 'tpl_spSaldo', 'Chunk', 0, 1, 0, '<script id=\"tpl_spSaldo\" type=\"text/x-jsrender\">\n    <span data-link=\"visible{:saldo!=0} class{:saldo > 0? \'text-success\':\'text-danger\'} html{formatMoney:saldo}\"></span>\n    <span class=\"text-muted\" data-link=\"visible{:saldo==0}\">Баланс: 0</span>\n</script>', 0, NULL, 0, ''),
(33, 0, 0, 'idExpiredTpl', 'Chunk', 0, 5, 0, '<div class=\"alert alert-warning\">\n    <b>Ошибка</b><br>\n    Пароль был уже изменен или ссылка просрочена.\n    Если вам нужно по-прежнему восстановить пароль, перейдите\n    <a href=\"/login/forgotpassword.html\">по ссылке</a>.\n</div>', 0, NULL, 0, ''),
(34, 0, 0, 'idLoginErrTpl', 'Chunk', 0, 5, 0, '<div class=\"alert alert-warning\">\n    [[+msg]]\n</div>', 0, NULL, 0, '');
INSERT INTO `mx_site_htmlsnippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `static`, `static_file`) VALUES
(35, 0, 0, 'idForgotPassEmail', 'Chunk', 0, 5, 0, '[[$mailBrand?content=`\n<p>Здравствуйте, [[+username]]</p>\n\n<p>Чтобы активировать свой новый пароль на сайте, перейдите по ссылке:</p>\n\n<p><a href=\"[[+confirmUrl]]\">[[+confirmUrl]]</a></p>\n\n<p>После активации вы сможете использовать свой новый пароль:</p>\n\n<p><strong>Пароль:</strong> [[+password]]</p>\n\n<p>Если вы не запрашивали восстановление пароля, проигнорируйте данное письмо.</p>\n`]]', 0, NULL, 0, ''),
(36, 0, 0, 'idForgotPassSentTpl', 'Chunk', 0, 5, 0, '<h4>Информация для входа на сайт отправлена</h4>\n<p>Информация для восстановления входа на сайт успешно отправлена по указанному адресу.</p>', 0, NULL, 0, ''),
(37, 0, 0, 'idForgotPassTpl', 'Chunk', 0, 5, 0, '<p>Для восстановления пароля, нам нужен Ваш адрес электронной почты, указанный при регистрации.</p>\n[[+loginfp.errors:notempty=`<div class=\"alert alert-warning\">[[+loginfp.errors]]</div>`]]\n<form class=\"form-inline1\" action=\"[[~[[*id]]]]\" method=\"post\">\n    <input type=\"hidden\" name=\"returnUrl\" value=\"[[+loginfp.request_uri]]\" />\n    <input type=\"hidden\" name=\"login_fp_service\" value=\"forgotpassword\" />\n    <div class=\"form-row\">\n        <div class=\"col-12 col-sm-auto\">\n            <input type=\"email\" name=\"email\" name=\"email\" value=\"[[+loginfp.post.email:default=`[[!#GET.email]]`]]\" \n                placeholder=\"Ваш e-mail\" class=\"form-control mb-2\" />\n        </div>\n        <div class=\"col-auto\">\n            <button class=\"btn btn-primary\" type=\"submit\">Сбросить пароль</button>\n        </div>\n    </div>\n</form>', 0, NULL, 0, ''),
(38, 0, 0, 'idResetPassTpl', 'Chunk', 0, 5, 0, '<p>Пароль для пользователя <b>[[+username]]</b> был сброшен.</p>  \n<a href=\"/login/\" class=\"btn btn-primary\">Войти в систему</a>', 0, NULL, 0, '');
INSERT INTO `mx_site_plugins` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `plugincode`, `locked`, `properties`, `disabled`, `moduleguid`, `static`, `static_file`) VALUES
(1, 0, 0, 'Ace', 'Ace code editor plugin for MODx Revolution', 0, 0, 0, '/**\n * Ace Source Editor Plugin\n *\n * Events: OnManagerPageBeforeRender, OnRichTextEditorRegister, OnSnipFormPrerender,\n * OnTempFormPrerender, OnChunkFormPrerender, OnPluginFormPrerender,\n * OnFileCreateFormPrerender, OnFileEditFormPrerender, OnDocFormPrerender\n *\n * @author Danil Kostin <danya.postfactum(at)gmail.com>\n *\n * @package ace\n *\n * @var array $scriptProperties\n * @var Ace $ace\n */\nif ($modx->event->name == \'OnRichTextEditorRegister\') {\n    $modx->event->output(\'Ace\');\n    return;\n}\n\nif ($modx->getOption(\'which_element_editor\', null, \'Ace\') !== \'Ace\') {\n    return;\n}\n\n$corePath = $modx->getOption(\'ace.core_path\', null, $modx->getOption(\'core_path\').\'components/ace/\');\n$ace = $modx->getService(\'ace\', \'Ace\', $corePath.\'model/ace/\');\n$ace->initialize();\n\n$extensionMap = array(\n    \'tpl\'   => \'text/x-smarty\',\n    \'htm\'   => \'text/html\',\n    \'html\'  => \'text/html\',\n    \'css\'   => \'text/css\',\n    \'scss\'  => \'text/x-scss\',\n    \'less\'  => \'text/x-less\',\n    \'svg\'   => \'image/svg+xml\',\n    \'xml\'   => \'application/xml\',\n    \'xsl\'   => \'application/xml\',\n    \'js\'    => \'application/javascript\',\n    \'json\'  => \'application/json\',\n    \'php\'   => \'application/x-php\',\n    \'sql\'   => \'text/x-sql\',\n    \'md\'    => \'text/x-markdown\',\n    \'txt\'   => \'text/plain\',\n    \'twig\'  => \'text/x-twig\'\n);\n\n// Define default mime for html elements(templates, chunks and html resources)\n$html_elements_mime=$modx->getOption(\'ace.html_elements_mime\',null,false);\nif(!$html_elements_mime){\n    // this may deprecated in future because components may set ace.html_elements_mime option now\n    switch (true) {\n        case $modx->getOption(\'twiggy_class\'):\n            $html_elements_mime = \'text/x-twig\';\n            break;\n        case $modx->getOption(\'pdotools_fenom_parser\'):\n            $html_elements_mime = \'text/x-smarty\';\n            break;\n        default:\n            $html_elements_mime = \'text/html\';\n    }\n}\n\n// Defines wether we should highlight modx tags\n$modxTags = false;\nswitch ($modx->event->name) {\n    case \'OnSnipFormPrerender\':\n        $field = \'modx-snippet-snippet\';\n        $mimeType = \'application/x-php\';\n        break;\n    case \'OnTempFormPrerender\':\n        $field = \'modx-template-content\';\n        $modxTags = true;\n        $mimeType = $html_elements_mime;\n        break;\n    case \'OnChunkFormPrerender\':\n        $field = \'modx-chunk-snippet\';\n        if ($modx->controller->chunk && $modx->controller->chunk->isStatic()) {\n            $extension = pathinfo($modx->controller->chunk->name, PATHINFO_EXTENSION);\n            if(!$extension||!isset($extensionMap[$extension])){\n                $extension = pathinfo($modx->controller->chunk->getSourceFile(), PATHINFO_EXTENSION);\n            }\n            $mimeType = isset($extensionMap[$extension]) ? $extensionMap[$extension] : \'text/plain\';\n        } else {\n            $mimeType = $html_elements_mime;\n        }\n        $modxTags = true;\n        break;\n    case \'OnPluginFormPrerender\':\n        $field = \'modx-plugin-plugincode\';\n        $mimeType = \'application/x-php\';\n        break;\n    case \'OnFileCreateFormPrerender\':\n        $field = \'modx-file-content\';\n        $mimeType = \'text/plain\';\n        break;\n    case \'OnFileEditFormPrerender\':\n        $field = \'modx-file-content\';\n        $extension = pathinfo($scriptProperties[\'file\'], PATHINFO_EXTENSION);\n        $mimeType = isset($extensionMap[$extension])\n            ? $extensionMap[$extension]\n            : (\'@FILE:\'.pathinfo($scriptProperties[\'file\'], PATHINFO_BASENAME));\n        $modxTags = $extension == \'tpl\';\n        break;\n    case \'OnDocFormPrerender\':\n        if (!$modx->controller->resourceArray) {\n            return;\n        }\n        $field = \'ta\';\n        $mimeType = $modx->getObject(\'modContentType\', $modx->controller->resourceArray[\'content_type\'])->get(\'mime_type\');\n\n        if($mimeType == \'text/html\')$mimeType = $html_elements_mime;\n\n        if ($modx->getOption(\'use_editor\')){\n            $richText = $modx->controller->resourceArray[\'richtext\'];\n            $classKey = $modx->controller->resourceArray[\'class_key\'];\n            if ($richText || in_array($classKey, array(\'modStaticResource\',\'modSymLink\',\'modWebLink\',\'modXMLRPCResource\'))) {\n                $field = false;\n            }\n        }\n        $modxTags = true;\n        break;\n    case \'OnTVInputRenderList\':\n        $modx->event->output($corePath . \'elements/tv/input/\');\n        break;\n    default:\n        return;\n}\n\n$modxTags = (int) $modxTags;\n$script = \'\';\nif ($field) {\n    $script .= \"MODx.ux.Ace.replaceComponent(\'$field\', \'$mimeType\', $modxTags);\";\n}\n\nif ($modx->event->name == \'OnDocFormPrerender\' && !$modx->getOption(\'use_editor\')) {\n    $script .= \"MODx.ux.Ace.replaceTextAreas(Ext.query(\'.modx-richtext\'));\";\n}\n\nif ($script) {\n    $modx->controller->addHtml(\'<script>Ext.onReady(function() {\' . $script . \'});</script>\');\n}', 0, NULL, 0, '', 0, 'ace/elements/plugins/ace.plugin.php'),
(2, 1, 0, 'pdoTools', '', 0, 2, 0, '/** @var modX $modx */\r\nswitch ($modx->event->name) {\r\n\r\n    case \'OnMODXInit\':\r\n        $fqn = $modx->getOption(\'pdoTools.class\', null, \'pdotools.pdotools\', true);\r\n        $path = $modx->getOption(\'pdotools_class_path\', null, MODX_CORE_PATH . \'components/pdotools/model/\', true);\r\n        $modx->loadClass($fqn, $path, false, true);\r\n\r\n        $fqn = $modx->getOption(\'pdoFetch.class\', null, \'pdotools.pdofetch\', true);\r\n        $path = $modx->getOption(\'pdofetch_class_path\', null, MODX_CORE_PATH . \'components/pdotools/model/\', true);\r\n        $modx->loadClass($fqn, $path, false, true);\r\n        break;\r\n\r\n    case \'OnSiteRefresh\':\r\n        /** @var pdoTools $pdoTools */\r\n        if ($pdoTools = $modx->getService(\'pdoTools\')) {\r\n            if ($pdoTools->clearFileCache()) {\r\n                $modx->log(modX::LOG_LEVEL_INFO, $modx->lexicon(\'refresh_default\') . \': pdoTools\');\r\n            }\r\n        }\r\n        break;\r\n\r\n    case \'OnWebPagePrerender\':\r\n        $parser = $modx->getParser();\r\n        if ($parser instanceof pdoParser) {\r\n            foreach ($parser->pdoTools->ignores as $key => $val) {\r\n                $modx->resource->_output = str_replace($key, $val, $modx->resource->_output);\r\n            }\r\n        }\r\n        break;\r\n}', 0, NULL, 0, '', 0, 'core/components/pdotools/elements/plugins/plugin.pdotools.php'),
(3, 0, 0, 'clubPackage', '', 0, 1, 0, '/*ini_set(\'error_reporting\', E_ALL);\nini_set(\'display_errors\', 1);\nini_set(\'display_startup_errors\', 1);*/\n\ndefine(\'CRM_PATH\', MODX_ASSETS_PATH.\'id/\');\ndefine(\'CRM_URL\', MODX_ASSETS_URL.\'id/\');\ndefine(\'CRM_FILES\', MODX_BASE_PATH.\'files/\');\n\ndefine(\'CRMTOOLS_PATH\', MODX_ASSETS_PATH.\'clubtools/\');\ndefine(\'CRMTOOLS_URL\', MODX_ASSETS_URL.\'clubtools/\');\ndefine(\'CRM_LOGS\', MODX_ASSETS_PATH.\'scrm_log/\');\n        \n//$modx->loadClass(\'modResource\'); // здесь потому что при сохранении в mgr \n$modx->map[\'modResource\'][\'fields\'][\'club_id\'] = 0;\n$modx->map[\'modResource\'][\'fieldMeta\'][\'club_id\'] = array(\n	\'dbtype\' => \'int\',\n	\'phptype\' => \'integer\',\n	\'null\' => false,\n	\'default\' => 0,\n);\n\nif ($modx->context->key == \'web\') {\n    $scrm = $modx->getService(\'scrm\', \'SCRM\', CRM_PATH);\n    $userID = $scrm->user; // TODO: Удалить\n\n    $cache_path = CRM_PREFIX.\'/clubConfig/init\';\n    $club_opts = $modx->cacheManager->get($cache_path);\n    if (empty($club_opts)) {\n        $club_opts = clubConfig(\'site_name,site_fullname,club_key,club_logo,club_modules,https,dbXML\', 0, 1);\n        if (!$club_opts[\'club_logo\']) {\n            $club_opts[\'club_logo\'] = CRM_URL.\'images/sportcrm_logo.png\';\n        }\n\n        $club_opts[\'club_modules\'] = empty($club_opts[\'club_modules\'])? array() : explode(\',\', $club_opts[\'club_modules\']);\n        array_unshift($club_opts[\'club_modules\'], \'clubStuff\', CRM_PREFIX);\n        $club_opts[\'crm_url\'] = CRM_URL;\n        $club_opts[\'crmtools_url\'] = CRMTOOLS_URL;\n        $modx->cacheManager->set($cache_path, $club_opts, 86400*3); // 3 дней\n    }\n    \n    if ($club_opts[\'dbXML\']) $scrm->dbLoad();\n    \n\n    if (!empty($club_opts[\'https\'])) {\n    ## Иногда, особенно на sweb надо добавлять в конфиг\n    ## $isSecureRequest = ((isset($_SERVER[\'HTTPS\']) && !empty($_SERVER[\'HTTPS\']) && strtolower($_SERVER[\'HTTPS\']) !== \'off\') || $_SERVER[\'SERVER_PORT\'] == $https_port || (isset($_SERVER[\'HTTP_X_FORWARDED_PROTO\']) && $_SERVER[\'HTTP_X_FORWARDED_PROTO\'] === \'https\'));\n        \n        $isHttps = (isset($_SERVER[\'HTTPS\']) && $_SERVER[\'HTTPS\'] === \'on\')\n        || (isset($_SERVER[\'REQUEST_SCHEME\']) && $_SERVER[\'REQUEST_SCHEME\'] === \'https\')\n        || (isset($_SERVER[\'HTTP_X_FORWARDED_PROTO\']) && $_SERVER[\'HTTP_X_FORWARDED_PROTO\'] === \'https\');\n        if (!$isHttps) {\n            $modx->sendRedirect(\'https://\'.$_SERVER[\'HTTP_HOST\'].$_SERVER[\'REQUEST_URI\'], array(\n                \'responseCode\' => \'HTTP/1.1 301 Moved Permanently\')\n            );\n        } else {\n            //$modx->setOption(\'site_url\', \'https://\'.$_SERVER[\'HTTP_HOST\']);\n            $modx->setOption(\'server_protocol\', \'https\');\n        }\n    }\n    \n    $club_opts[\'club_name\'] = $club_opts[\'site_name\']; // TODO: Надо ли?\n    $club_opts[\'scrm_ugroups\'] = $userGroups = $scrm->userGroups;\n    $club_opts[\'scrm_grhash\'] = empty($scrm->userGroups)? \'empty\' : cacheHash($scrm->userGroups);\n    $club_opts[\'scrm_prugv\'] = cacheHash([ CRM_PREFIX, $scrm->user, $_SESSION[\'scrm_thislogin\'], $modx->getOption(\'vers\') ]);\n    \n    foreach($club_opts as $okey => $oval) {\n        $modx->setOption($okey, $oval);\n    }\n    $club_opts[\'site_url\'] = $modx->getOption(\'site_url\');\n    $modx->setPlaceholders($club_opts, \'+\');\n    \n    $rq = explode(\'/\', $_REQUEST[\'q\']);\n    \n    $club_modules = implode(\',\', $club_opts[\'club_modules\']);\n    define(\'CRM_START\', \'{\'. implode(\',\', [CRMTOOLS_PATH, CRM_PATH]) .\'}start/{\'. $club_modules .\'}_\');\n    foreach (glob(CRM_START.\'init.php\', GLOB_BRACE) as $data_handler) require($data_handler);\n\n    include(CRM_PATH.\'hook.php\');\n}', 0, 'a:0:{}', 0, '', 0, ''),
(5, 1, 0, 'afterLogin', '', 0, 5, 0, '$ugroups = $user->getUserGroupNames();\n$_SESSION[\'club_groups\'] = implode(\';\', $ugroups);\n$_SESSION[\'scrm_thislogin\'] = date(\'c\');\n\nif (in_array(\'idAdmin\', $ugroups)) $_SESSION[\'club_admin\'] = 1;\nif (in_array(\'idManager\', $ugroups)) $_SESSION[\'club_manager\'] = 1;\n$_SESSION[\'club_debug\'] = $user->isMember(\'Administrator\');\n\nif (($userprofile = $user->getOne(\'Profile\')) !== null) {\n    $_SESSION[\'user_fullname\'] = $userprofile->get(\'fullname\');\n    $_SESSION[\'club_cityname\'] = $userprofile->get(\'city\');\n    $modx->loadClass(\'idExtid\');\n    $modx->map[\'idExtid\'][\'aggregates\'][\'idCity\'] = array (\n        \'class\' => \'idCity\',\n        \'local\' => \'extid\',\n        \'foreign\' => \'id\',\n        \'cardinality\' => \'one\',\n        \'owner\' => \'foreign\',\n    );\n    \n    $idUser_city = getClubExtId(array(\n        \'parent\' => $user->get(\'id\'),\n    ), \'idUser_city\', true, \'idCity\');\n\n    if (!empty($idUser_city)){\n        $_SESSION[\'club_city\'] = $idUser_city->idCity->get(\'id\');\n        $_SESSION[\'club_cityname\'] = $idUser_city->idCity->get(\'name\');\n    }\n}\n\n$returnUrl = $modx->getOption(\'returnUrl\', $scriptProperties, $_POST[\'returnUrl\']);\n//$returnUrl = $modx->getOption(\'returnUrl\', $_POST, $returnUrl);\n\n//$modx->log(modX::LOG_LEVEL_ERROR, \"afterLogin: $returnUrl; url:\".$_SERVER[\'HTTP_HOST\'].$_SERVER[\'REQUEST_URI\'].\'; user:\'.$user->get(\'id\'));\n\nif (empty($returnUrl)) {\n    $modx->runSnippet(\'loginWay\', array(\n        \'user\' => $user,\n    ));\n}', 0, 'a:0:{}', 0, '', 0, ''),
(6, 1, 0, 'clubScripts', '', 0, 1, 0, '$res = $modx->resource;\n$start = \'\'; $bottom = \'\'; $app = array(\n    \'club_id\' => $res->get(\'club_id\'),\n    \'club_city\' => $_SESSION[\'club_city\'],\n);\nif (defined(\'CRM_PREFIX\') && !empty($res->get(\'template\'))) {\n    //$modx->log(modX::LOG_LEVEL_ERROR,\"Log2 \".json_encode($res->toArray(), JSON_UNESCAPED_UNICODE));\n\n    $cfg = clubConfig(\'StartupHTMLBlock,BottomHTMLBlock\'); //grid_rows\n    $start = $cfg[\'StartupHTMLBlock\'];\n    $bottom = $cfg[\'BottomHTMLBlock\'];\n    //$app[\'res\'] = $res->toArray(\'\',false,false,true);\n    //$app[\'res\'][\'content\'] = null;\n\n    $app[\'ugroups\'] = $ugroups = $modx->getOption(\'scrm_ugroups\');\n    if (!empty($ugroups)){\n        $app[\'is_crew\'] = isCrew();\n    }\n    \n    $club_modules = $modx->getOption(\'club_modules\');\n    $clubStatus = getClubStatus(array(\n        \'tbl\' => \'idModule\',\n        \'cls:IN\' => $club_modules,\n    )); //idPermission\n    \n    //$bottom .= \'111\'.json_encode($club_modules);\n    $userID = $modx->scrm->user;\n    $load_modules = array();\n    $pathReplace1 = array(\'{crm}\',\'{modules}\',\'{vers}\',\'{user}\', \'{prugv}\');\n    $pathReplace2 = array(\n        CRM_URL,\n        \'/handlers/modules/\',\n        \'v=\'.$modx->getOption(\'vers\'),\n        $userID,\n        $modx->getOption(\'scrm_prugv\'),\n    );\n    \n    foreach($clubStatus as $rowMod) {\n        if ($rowMod[\'tbl\'] != \'idModule\') continue;\n        $ext = $rowMod[\'extended\'];\n        //if (!in_array($rowMod[\'cls\'], $club_modules)) continue;\n        if (!empty($ext[\'group\'])) {\n            if (empty($ugroups)) continue;\n            $check_groups = array_merge(array(\'all\'), $ugroups);\n            if (empty(array_intersect(explode(\',\', $ext[\'group\']), $check_groups))) continue;\n        }\n        if (!empty($ext[\'club_id\']) && !in_array($app[\'club_id\'], explode(\',\', $ext[\'club_id\']))) continue;\n        if (!empty($ext[\'script\'])) {\n            $bottom .= \'<script src=\"\'. str_replace($pathReplace1, $pathReplace2, $ext[\'script\']) .\'\"></script>\';\n        }\n        if (!empty($ext[\'load\'])) {\n            $load_modules[] = str_replace($pathReplace1, $pathReplace2, $ext[\'load\']);\n        }\n    }\n    if (!empty($load_modules)) {\n        $bottom .= \'<script>$(function(){SCRM.loadSeries(\'.json_encode($load_modules).\');});</script>\';\n    }\n\n    foreach (glob(CRM_START.\'load.php\', GLOB_BRACE) as $data_handler) require($data_handler);\n}\n\n$modx->setPlaceholders(array(\n    \'StartupHTMLBlock\' => \'<script>$.extend(SCRM.app, \'. json_encode($app) .\');</script>\'.PHP_EOL.$start,\n    \'BottomHTMLBlock\' => $bottom,\n));', 0, 'a:0:{}', 0, '', 0, ''),
(7, 0, 0, 'clubHandleRequest', '', 0, 1, 0, '//if ($modx->event->name != \'OnPageNotFound\') {return false;}\n// echo json_encode($json, JSON_UNESCAPED_UNICODE);\n//if (defined(\'CLUB_FORWARD\')) return false;\n//define(\'CLUB_FORWARD\', 1);\n\n$rq = explode(\'/\', $_REQUEST[\'q\']);\nif ($rq[0]==\'qr.html\') {\n    if (!$qr = $modx->findResource(\'qr/\')) return false; // если нет раздела выход\n    $modx->sendForward($qr);\n}\n/*if ($rq[0]==\'start\') {\n    $modx->resource = $modx->newObject(\'modResource\');\n    $modx->resource->set(\'cacheable\', 0);\n    \n    //include(\'eform.php\');\n    \n    $modx->resource->set(\'pagetitle\', $modx->getOption(\'title\', $json, \'33Start!!\'));\n    \n    $modx->setPlaceholder(\'pageBody\', \'He423lllo!\');\n    $modx->sendForward($modx->findResource(\'start/\'), array(\n       \'merge\' => 1,\n       \'forward_merge_excludes\' => \'template,type\'\n    ), false);\n}*/', 0, NULL, 0, '', 0, ''),
(8, 0, 0, 'bitBtn', '', 0, 1, 0, '$modx->controller->addHtml(\"<script>\nfunction timeout(){\n    setTimeout(function() {\n        if (Ext.getCmp(\'modx-action-buttons\') != undefined) {\n            var toolBar = Ext.getCmp(\'modx-action-buttons\');\n            var buttons = [];\n            buttons.push({\n                text: \'.git\',\n                handler: function () {\n                    var commit = prompt(\'Описание версии\'),\n                    folder;\n                    if ( commit != undefined ) {\n                        var json = {\n                            commit: commit\n                        };\n                        if ( Ext.getCmp(\'modx-resource-content\' ) != undefined ) {\n                            json.resid = MODx.request.id;\n                        } else if ( Ext.getCmp(\'modx-file-content\' ) != undefined ) {\n                            json.folder = Ext.getCmp(\'modx-file-name\').value;\n                        } else if ( Ext.getCmp(\'modx-template-content\' ) != undefined  ) {\n                            json.tmplid = MODx.request.id;\n                        } else if ( Ext.getCmp(\'modx-plugin-plugincode\') != undefined  ) {\n                            json.pluginid = MODx.request.id;\n                        }\n                        //var json = JSON.stringify(json);\n                        Ext.Ajax.request({\n                           url: \'/hook/bitbucket\', // where you wanna post\n                           success: function() {\n                                alert(\'Saved\');\n                           },   // function called on success\n                           failure: function() {\n                                alert(\'Failed\');\n                           },\n                           params: json  // your json data\n                        });\n                        /*var xhr = new XMLHttpRequest();\n                        var json = JSON.stringify(json);\n                        xhr.open(\'POST\', \'/hook/bitbucket\', true)\n                        xhr.setRequestHeader(\'Content-type\', \'application/json; charset=utf-8\');\n                        xhr.send(json);*/\n                    }\n                }\n            });\n            toolBar.addButton(buttons);\n            toolBar.doLayout();\n        } else {\n            timeout();\n        }\n    }, 100)\n}\ntimeout();\n</script>\");', 0, NULL, 0, '', 0, '');
