(26, 1, 0, 'dbvalues', '', 0, 1, 0, '$json = array(\n    \'club_city\' => $_SESSION[\'club_city\'],    \n);\n\n$mode = $modx->getOption(\'mode\', $scriptProperties, $_REQUEST[\'mode\']);\n$city = $modx->getOption(\'city\', $scriptProperties, $_REQUEST[\'city\']);\n$lessdata = $modx->getOption(\'lessdata\', $scriptProperties, $_REQUEST[\'lessdata\']);\n\nif ($city == \'my\') $city = $_SESSION[\'club_city\'];\n$mode = empty($mode)? array() : explode(\',\', $mode);\n\nif (in_array(\'idLevel\', $mode)) {\n    $q = $modx->newQuery(\'idLevel\');\n    $q->sortby(\'name\',\'ASC\')->select(array(\'idLevel.*\'));\n    $stmt = $q->prepare();\n    $stmt->execute();\n    $json[\'idLevel\'] = $stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\nif (in_array(\'idInvoiceType\', $mode)) {\n    $q = $modx->newQuery(\'idInvoiceType\', array(\n        \'published\' => 1,\n    ));\n    $stype = $modx->getOption(\'stype\', $scriptProperties, $_REQUEST[\'stype\']);\n    if (empty($stype)) {\n        $q->leftJoin(\'idSInvType\');\n    } else {\n        $q->innerJoin(\'idSInvType\');\n        $q->where(array(\n            \'idSInvType.stype\' => $stype,\n        ));\n    }\n    $q->groupby(\'idInvoiceType.id\');\n    $q->sortby(\'menuindex\',\'ASC\');\n    $q->sortby(\'name\',\'ASC\');\n    $q->select(array(\n        \'idInvoiceType.*\', // Так выдает нормально все поля\n        \"GROUP_CONCAT(idSInvType.stype SEPARATOR \',\') as stype\",\n        //\'idSInvType.stype\',\n    ));\n    $q->prepare(); //$json[\'sqq\'] = $q->toSQL();\n    $q->stmt->execute();\n    $json[\'idInvoiceType\'] = $q->stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\nif (empty($mode) || in_array(\'idSport\', $mode)) {\n    $q = $modx->newQuery(\'idSport\', array(\n        \'published\' => 1,\n    ));\n    $q->sortby(\'menuindex\',\'ASC\');\n    $q->sortby(\'name\',\'ASC\');\n    $q->select(array(\'idSport.*\'));\n    $stmt = $q->prepare();\n    $stmt->execute();\n    $json[\'idSport\'] = $stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\nif (empty($mode) || in_array(\'idTrainer\', $mode)) {\n    $q = $modx->newQuery(\'idTrainer\', array(\n        \'published\' => 1,\n        \'name:!=\' => \'_\',\n    ));\n    $q->sortby(\'name\',\'ASC\');\n    $tr_select = array(\'idTrainer.id\', \'idTrainer.name\', \'idTrainer.city\', \'idTrainer.uid\', \'gender\', \'categ\', \'iduser\');\n    if (!$lessdata) {\n        $q->select(\'content\');\n        $q->groupby(\'idTrainer.id\');\n        $q->leftJoin(\'idFiles\', \'idFiles\', array(\'idFiles.uid = idTrainer.uid\', \"idFiles.filetype=\'tr_photo\'\"));\n        $tr_select[] = \"GROUP_CONCAT(CONCAT_WS(\'.\', idFiles.key, idFiles.fileext, idFiles.incloud) SEPARATOR \',\') as photo\";\n    }\n    $q->select($tr_select);\n    \n    if (!empty($city)) $q->where(array(\'city\' => $city));\n    $stmt = $q->prepare();\n    $stmt->execute();\n    $json[\'idTrainer\'] = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    \n    if (!$lessdata) {\n        $scrmFiles = $modx->getService(\'scrmfiles\', \'scrmFiles\', CRM_PATH);\n        $thumb_size = $modx->getOption(\'size\', $scriptProperties, \'md\');\n        foreach($json[\'idTrainer\'] as $n => $v) {\n            $fn = explode(\',\', $v[\'photo\'])[0];\n            $json[\'idTrainer\'][$n][\'photo\'] = !$fn ? $scrmFiles->emptyImg($v[\'gender\'], true) : $scrmFiles->fileUrlFromString($fn, \'tr_photo\', $thumb_size);\n        }\n    }\n}\n\nif (in_array(\'idSquad\', $mode)) {\n    $q = $modx->newQuery(\'idSquad\', array(\n        \'published\' => 1,\n    ));\n    if (!empty($city)) {\n        $q->where(array(\'idClub.city\' => $city));\n        $q->innerJoin(\'idClub\');\n    }\n    $q->leftJoin(\'idLevel\');\n    $q->leftJoin(\'idSport\');\n    $q->sortby(\'lvl_name\',\'ASC\');\n    $q->sortby(\'idSquad.name\',\'ASC\');\n    $q->select(array(\n        \'idSquad.id\',\n        \'idSquad.name\',\n        \'club\',\n        \"CONCAT_WS(\', \', idLevel.name, idSquad.name) as squad_name\",\n        \'idLevel.name as lvl_name\',\n        \'idSport.name as sport_name\',\n        \'idSport.shortname as ssport_name\'\n    ));\n    $stmt = $q->prepare();\n    $stmt->execute();\n    \n    $json[\'idSquad\'] = $stmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\nif (empty($mode) || in_array(\'idClub\', $mode)) {\n    $q = $modx->newQuery(\'idClub\', array(\n        \'published\' => 1,\n    ));\n    if (!empty($city)) $q->where(array(\'city\' => $city));\n    $q->sortby(\'name\',\'ASC\');\n    $q->select(explode(\',\',\'id,name,address,city\'));\n    $stmt = $q->prepare();\n    $stmt->execute();\n    \n    $json[\'idClub\'] = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    if (!empty($json[\'idSquad\'])) {\n        foreach ($json[\'idClub\'] as $idx => $club) {\n            $inner = array();\n            foreach($json[\'idSquad\'] as $row) {\n                if ($row[\'club\'] == $club[\'id\']) $inner[] = $row;\n            }\n            $json[\'idClub\'][$idx][\'idSquad\'] = $inner;\n        }\n        //unset($json[\'idSquad\']);\n    }\n}\n\nif (empty($mode) || in_array(\'idCity\', $mode)) {\n    $q = $modx->newQuery(\'idCity\', array(\n        \'published\' => 1,\n    ));\n    if (!empty($city)) $q->where(array(\'id\' => $city));\n    $q->sortby(\'menuindex\',\'ASC\');\n    $q->sortby(\'name\',\'ASC\');\n    $q->select(explode(\',\',\'id,name,fullname,alias,inn,kpp,bankbik,bankrs,bankks,chief,email,tel\'));\n    $stmt = $q->prepare();\n    $stmt->execute();\n    $json[\'idCity\'] = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    foreach ($json[\'idCity\'] as $idx => $city){\n        if (!empty($json[\'idClub\'])) {\n            $inner = array();\n            foreach($json[\'idClub\'] as $idClub) {\n                if ($idClub[\'city\'] == $city[\'id\']) $inner[] = $idClub;\n            }\n            $json[\'idCity\'][$idx][\'idClub\'] = $inner;\n        }\n        \n        if (!empty($json[\'idTrainer\'])) {\n            $inner = array();\n            foreach($json[\'idTrainer\'] as $idTrainer) {\n                if ($idTrainer[\'city\'] == $city[\'id\']) $inner[] = $idTrainer;\n            }\n            $json[\'idCity\'][$idx][\'idTrainer\'] = $inner;\n        }\n    }\n    unset($json[\'idClub\']);\n    unset($json[\'idTrainer\']);\n}\n\nif (!empty($placeholder)) $modx->setPlaceholder($placeholder, $json);\nclubAllowOrigin();\nreturn json_encode($json, JSON_UNESCAPED_UNICODE);', 0, 'a:0:{}', '', 0, '');
INSERT INTO `mx_site_snippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`, `static`, `static_file`) VALUES
(27, 0, 0, 'idGetSportsmen', '', 0, 1, 0, '$json = array(\n    \'key\' => $key = $modx->getOption(\'key\', $scriptProperties, $_REQUEST[\'key\']),\n);\n\n$mode = $modx->getOption(\'mode\', $scriptProperties, $_REQUEST[\"mode\"]);\n\n$wsp = $modx->newQuery(\'idSportsmen\', array(\'idSportsmen.key\' => $key));\n$wsp->innerJoin(\'idStatus\', \'Gender\');\n$wsp->leftJoin(\'idTrainer\');\n$wsp->leftJoin(\'idSquad\', \'idSquad\', \"idSquad.id = idSportsmen.squad AND idSquad.published = 1\");\n$wsp->leftJoin(\'idLevel\', \'idLevel\', \'idLevel.id = idSquad.lvl\');\n$wsp->leftJoin(\'idClub\', \'idClub\', \'idClub.id = idSquad.club\');\n$wsp->leftJoin(\'idSport\', \'idSport\', \'idSport.id = idSquad.sport\');\n$wsp->leftJoin(\'modUser\', \'idUser\', \"idUser.id = idSportsmen.iduser\");\n$wsp->select(array(\n    \'idSportsmen.*\',\n    \'Gender.name as gender_name\',\n    \'idUser.username\',\n    \'idTrainer.name as trainername\',\n    \'idSquad.name as squad_name\',\n    \'idSquad.club as club\',\n    \'idLevel.name as level_name\',\n    \'idClub.name as club_name\',\n    \'idSport.name as sport_name\',\n));\n$sp = $modx->getObject(\'idSportsmen\', $wsp);\n//$sp = $modx->getObjectGraph(\'idSportsmen\', \'{\"idCity\":{}}\', $wsp);\n\nif (empty($sp)){\n    $redirect = $modx->getOption(\'redirect\', $scriptProperties, false);\n    if (!empty($redirect)) $modx->sendRedirect(\'/\');\n} else {\n    $json = $sp->toArray();\n\n    $json[\'opts\'] = $opts = clubConfig(\'paym,hide0paym,idSportsmen_arc\');\n    $payms = explode(\',\', $opts[\'paym\']);\n    //$json[\'club_residue\'] = $opts[\'club_residue\'];\n    \n    $wi = $modx->newQuery(\'idInvoice\', array(\n        \'sportsmen\' => $json[\'id\'],\n        \'duedate > NOW()\'\n    ));\n    $wi->innerJoin(\'idInvoiceType\');\n    $wi->leftJoin(\'idSInvType\');\n    $wi->leftJoin(\'idLesson\', \'lsn\', \'lsn.idinvoice = idInvoice.id\');\n    $wi->select(array(\n        \'idInvoice.id\',\n        \'idInvoice.duedate\',\n        \'idInvoiceType.name as invoice_name\',\n        \'idInvoice.lesson_amount\',\n        \'GROUP_CONCAT(DISTINCT idSInvType.stype) as stype\',\n        \'COUNT(DISTINCT lsn.id) as cnt_lesson\',\n    ));\n    $wi->sortby(\'idInvoice.duedate\', \'asc\');\n    $wi->groupby(\'idInvoice.id\');\n    $wi->having(\'(lesson_amount=0) OR (lesson_amount > cnt_lesson)\');\n    $stmt = $wi->prepare();\n    //$json[\'sqlidSInvType\'] = $wi->toSQL();\n    $stmt->execute();\n    $json[\'rest_lesson\'] = 0;\n    $json[\'InvoiceLesson\'] = array();\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        $json[\'InvoiceLesson\'][] = $row;\n        $json[\'duedate\'] = $row[\'duedate\'];\n        if (!empty($row[\'lesson_amount\']))\n            $json[\'rest_lesson\'] += ($row[\'lesson_amount\'] - $row[\'cnt_lesson\']);\n        $json[\'lesson_amount\'] = $row[\'lesson_amount\'];\n    }\n    \n    /* --- idSportsmenSquad --- */\n    $wsq = $modx->newQuery(\'idSportsmenSquad\', array(\n        \'sportsmen\' => $json[\'id\'],\n        //\'dateend\' => null,\n    ));\n    \n    $wsq->innerJoin(\'idSquad\');\n    $wsq->leftJoin(\'idSportsmen\', \'sp\', \'sp.id=idSportsmenSquad.sportsmen AND idSportsmenSquad.squad=sp.squad\');\n    $wsq->leftJoin(\'idLevel\', \'idLevel\', \"idLevel.id = idSquad.lvl\");\n    $wsq->leftJoin(\'idClub\', \'idClub\', \"idClub.id = idSquad.club\");\n    $wsq->leftJoin(\'idSport\', \'idSport\', \"idSport.id = idSquad.sport\");\n    $wsq->select(array(\n        \"idSportsmenSquad.*\",\n        \"idSquad.club\",\n		\"idSquad.name as squad_name\",\n		\"idLevel.name as level_name\",\n		\"idClub.name as club_name\",\n		\"idSport.name as sport_name\",\n		\'sp.squad as is_main\',\n    ));\n    $wsq->sortby(\'is_main\', \'desc\');\n    $wsq->sortby(\'dateend\', \'desc\');\n    $wsq->sortby(\'created\', \'desc\');\n    //$wsq->sortby(\'idLevel.name\', \'asc\');\n    //$wsq->sortby(\'idSquad.name\', \'asc\');\n    $stmt = $wsq->prepare();\n    \n    //$json[\'sssql\'] = $wsq->toSQL();\n    $stmt->execute();\n    $json[\'idSportsmenSquad\'] = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    \n    $wcn = $modx->newQuery(\'idContact\', array(\n        \'sportsmen\' => $json[\'id\'],\n    ));\n    $wcn->select(array(\'idContact.*\')); // Массив + Таблица = выдает поля нормально\n    $wcn->sortby(\'name\', \'asc\');\n    $stmt = $wcn->prepare();\n    //$json[\'sql33\'] = $wcn->toSQL();\n    $stmt->execute();\n    $json[\'idContact\'] = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    $idCity = $sp->getOne(\'idCity\');\n    if (!empty($idCity)) {\n        $json[\'idCity\'] = $idCity->toArray();\n        if (empty($_SESSION[\'club_city\'])) $modx->runSnippet(\'setUserCity\', array(\n            \'idCity\' => $idCity,\n        ));\n    }\n\n    //$modx->setPlaceholders($json, \'ids.\');\n    \n    // $json[\'total\'] = $_REQUEST[\'total\'];\n    // $json[\'paym\'] = $modx->getOption(\'paym\', $scriptProperties, $_REQUEST[\'paym\']);\n    if ($mode == \'pay\') {\n        $paym = $modx->getOption(\'paym\', $scriptProperties, $_REQUEST[\'paym\']);\n        if (empty($paym)) $paym = $payms[0];\n        if (!empty($paym)) {\n            $paydata = getClubConfig($paym);\n            $paydata = (empty($paydata))? array() : json_decode($paydata, true);\n            //$json[\'pd\'] = $paydata;\n            $paydata[\'sportsmen\'] = $json[\'id\'];\n            $paydata[\'total\'] = $modx->getOption(\'total\', $_REQUEST, abs($json[\'saldo\']))*1;\n            $paydata[\'email\'] = $modx->getOption(\'email\', $_REQUEST, $json[\'email\']);\n            $site_url = $modx->getOption(\'site_url\');\n            $paydata[\'backUrl\'] = $site_url .\"sportsmens/sportsmen.html?key=\". $json[\'key\'];\n\n            if (empty($paydata[\'tmpl\'])) $paydata[\'tmpl\'] = \'Оплата по договору [[+contract]]<br>[[+name]]\';\n            $paydata[\'paytext\'] = clubTmpl($paydata[\'tmpl\'], $json);\n\n            $idPay = $modx->newObject(\'idPay\', array(\n                \'sportsmen\' => $json[\'id\'],\n                \'sum\' => $paydata[\'total\'],\n                \'payd\' => 0,\n            ));\n            \n            if (!empty($paydata[\'multiply\'])) $paydata[\'total\'] += $paydata[\'total\']*$paydata[\'multiply\'];\n            \n            /*$paydata[\'items\'] = array(\n                array(\n                    \'price\' => $paydata[\'total\'],\n                    \'total\' => $paydata[\'total\'],\n                    \'amount\' => 1,\n                    \'name\' => $paydata[\'paytext\'],\n                ),    \n            );*/\n            \n            $paym_file = CRM_PATH. \'hook/\' .$paym. \'.php\';\n            if (file_exists($paym_file)) require($paym_file);\n            \n            $json[\'paydata\'] =  $paydata;\n        }\n    } else {\n        $modx->runSnippet(\'clubFiles\', array(\n            \'uid\' => $json[\'key\'],\n            \'cls\' => \'idSportsmen\',\n            \'thumb\' => true,\n        ));\n        $json[\'files\'] = $modx->getPlaceholder(\'clubFiles\');\n    }\n    \n    # ================= Новые методы оплаты\n    $wPaym = $modx->newQuery(\'idStatus\', array(\n        \'idStatus.tbl\' => \'idPayMethod\',\n        \'idStatus.published\' => 1,\n        \'idCity_Pay.parent:IN\' => array(0, $sp->get(\'city\')) ,\n    ));\n    $wPaym->innerJoin(\'idStatus\', \'idExType\', array(\n        \'idExType.tbl\' => \'idExtid\', \n        \'idExType.alias\' => \'idCity_Pay\',\n    ));\n    $wPaym->innerJoin(\'idExtid\', \'idCity_Pay\', array(\n        \'idCity_Pay.extid = idStatus.id\',\n        \'idCity_Pay.extype = idExType.id\',\n    ));\n    $wPaym->sortby(\'idStatus.menuindex\');\n    $wPaym->sortby(\'idStatus.name\');\n    foreach($modx->getIterator(\'idStatus\', $wPaym) as $payMethod) {\n        if (empty($json[\'selPayMethod\'])) $json[\'selPayMethod\'] = (string)$payMethod->get(\'id\');\n        $json[\'idPayMethod\'][] = $payMethod->toArray();\n    }\n    # ================= END Новые методы оплаты\n    \n    if ($mode == \'cabinet\') {\n        $json[\'clubStatus\'] = getClubStatus(\'idSchedule,idLesson\');\n        $squads = array_map(\n            function($sq) {\n                if (!empty($sq[\'published\'])) return \'club\'.$sq[\'club\'];\n            },\n            $json[\'idSportsmenSquad\']\n        );\n        if (!empty($json[\'club\'])) $squads[] = \'club\'.$json[\'club\'];\n        \n        if (!empty($squads)) {\n            $modx->runSnippet(\'clubFiles\', array(\n                \'uid\' => implode(\',\', $squads),\n                \'cls\' => \'idClub\',\n            ));\n            $json[\'clubDocs\'] = $modx->getPlaceholder(\'clubFiles\');\n        }\n        \n        if (empty($opts[\'hide0paym\']) || $json[\'saldo\']*1 < 0) {\n            //$idCity_Pay = getClubStatusAlias(\'idExtid\',\'idCity_Pay\');\n            //$json[\'idCity_Pay\'] = $idCity_Pay;\n\n            if (!empty($payms[0])) {\n                foreach ($payms as $paym) {\n                    $paym_file = CRM_PATH. \'hook/\' .$paym. \'.php\';\n                    if (file_exists($paym_file)) require($paym_file);\n                }\n            }\n        }\n    }\n}\n\n//$json[\'created\'] = \n$json[\'now\'] = date(\'c\');\n\nif ($mode==\'export\'){\n    $modx->runSnippet(\'createDoc\', array(\n        \'data\' => $json,\n        \'handler\' => \'idSportsmen\',\n        \'tmpl\' => $modx->getOption(\'tmpl\', $_REQUEST, \'sportsmen_contract.docx\'),\n    ));\n}\n\n$modx->setPlaceholder(\'idGetSportsmen\', $json);\nreturn json_encode($json, JSON_UNESCAPED_UNICODE);', 0, NULL, '', 0, ''),
(28, 0, 0, 'crmFormIt', '', 0, 1, 0, '$data = $_REQUEST;\nif (!empty($hook)){\n    $data = $hook->getValues();\n}/* else {\n    $token = $modx->getOption(\'club_key\');\n    if (isset($_REQUEST[\'club_key\']) && ($_REQUEST[\'club_key\'] == $token)) {\n        \n    }\n}*/\n\nif (!empty($data)){\n    $data[\'oper\'] = \'add\';\n    $data[\'table\'] = \'idLead\';\n    $squad = explode(\'_\', $data[\'squad\']);\n    $data[\'squad\'] = $squad[0];\n    if (sizeof($squad)>1) $data[\'club\'] = $squad[1];\n    \n    $dbedit_out = $modx->runSnippet(\'dbedit\', array(\n        \'data\' => $data,\n    ));\n    $d_out = $modx->fromJSON($dbedit_out);\n    $modx->runSnippet(\'dbedit\', array(\n        \'data\' => array(\n            \'oper\' => \'add\',\n            \'table\' => \'idMsg\',\n            \'info\' => $modx->getChunk(\"mailNew_idLead\", $d_out[\'data\']),\n            \'msg_to\' => $data[\'email\'],\n            \'msg_subj\' => \'Приглашаем на просмотр\',\n        )    \n    ));\n    if (!empty($hook) && !empty($d_out)){\n        $hook->fields[\'key\'] = $d_out[\'data\'][\'key\'];\n    }\n    file_get_contents(\'https://w.sportcrm.club/hook/wcatchcrmformit?\'.http_build_query(\n        array(\n            \'site\' => $_SERVER[\'HTTP_HOST\'],\n            \'club\' => $data[\'club\'],\n            \'email\' => $data[\'email\']\n        )\n    ));\n}\n\nreturn (!empty($hook))? true : $dbedit_out;', 0, NULL, '', 0, ''),
