INSERT INTO `ur_income` (`id`, `num`, `status`, `source`, `name`, `medobj`, `info`, `totype`, `tel`, `email`, `zip`, `address`, `created`, `author`, `dateoutcome`, `idtype`) VALUES
(1, 'БН-266', 10, 0, 'Иванов Игнат', 0, 'Довожу до Вашего сведения, что г. на горячую линию обратилась клиент ФИО, телефон) с жалобой на ПЗ Сочи.  Клиент (дата) обратилась в указанный ПЗ с вопросом сдачи биоматериала на ряд исследований , в том числе комплексных пакетов.  Клиент недовольна тем, что в ПЗ пациенты находились без масок и перчаток, сотрудник на регистратуре также работала без перчаток. Клиент также недовольна тем, что сотрудник разговаривала с клиентом нехотя, отказывалась консультировать клиента, объясняя отказ тем, что «зависает» компьютер, пока клиент не пригрозила оставить жалобу. В середине разговора сотрудник ушла в туалет. После посещения туалета сотрудник не обработала руки при пациенте.  Клиент просит разобраться и принять меры.   Ждет звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:44', 1, NULL, 2),
(2, 'БН-872', 11, 0, 'Петров Петр', 0, 'Довожу до Вашего сведения, что (дата) на горячую линию обратился клиент (ФИО, тел.) с жалобой на ПЗ. Клиент обратился  в указанный ПЗ (дата) для сдачи биоматериала на ряд исследований. Клиент предъявил сотруднику ПЗ 2 подарочных сертификата арендодателям номиналом 1000р. каждый для оплаты заказа. Клиент недоволен тем, что сотрудник в общении с клиентом позволяла себе «хамство», отказалась принимать сертификаты в качестве оплаты.   Клиент просит разобраться и принять меры.   Ждет звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:44', 1, NULL, 4),
(3, 'БН-562', 10, 0, '', 0, 'Довожу до Вашего сведения, что (дата) на горячую линию обратилась клиент (ФИО, тел.) с жалобой на правила предоставления услуги «Выезд на дом». Клиент оформила услугу «Выезд на дом медицинской сестры» на (дата) Клиент недовольна тем, что ожидать медсестру необходимо с 7:00 до 12:00.  Клиент не смогла дождаться медицинской сестры по причине приема лекарственных препаратов по беременности и отменила вызов.  Клиент просит разобраться и принять меры.   Ждет звонка администрации', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(4, 'БН-192', 10, 0, '', 0, 'Довожу до Вашего сведения, что (дата) на горячую линию обратилась клиент (ФИО, тел.) с жалобой на ПЗ.  Клиент недовольна качеством обслуживания. Клиенту удобно обращаться в указанный ПЗ. Клиент недовольна тем, что сотрудник общается неприветливо, не отвечает на вопросы   клиента по   подготовке и правилах сбора и хранения биоматериала. Клиент просит разобраться и принять меры. Ждет   звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(5, 'БН-273', 10, 0, '', 0, 'Довожу до Вашего сведения, что (дата) на горячую линию обратился клиент (ФИО, тел.) с жалобой на ПЗ. Жена клиента ФИО  обратилась  в указанный ПЗ  (дата) по предварительной записи на УЗИ – обследование.  Клиент недоволен тем, что врач УЗИ в этот день не вел прием.  Клиент просит разобраться и принять меры. Ждет   звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(6, 'БН-790', 10, 0, '', 0, 'Довожу до Вашего сведения, что (дата) на горячую линию обратилась клиент (ФИО, тел.) с жалобой на ПЗ.  Клиент обратилась  в указанный ПЗ (дата) во второй половине для сдачи биоматериала  своему ребенку.   Клиент недовольна тем, что в указанном ПЗ ведется прием на исследование «Коронавирус». Клиент также недовольна тем, что на сайте компании и в других источниках не размещена информация о режиме приема биоматериала на стандартные исследования и на исследование «Коронавирус». Клиент просит разобраться и принять меры. Ждет   звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(7, 'БН-133', 10, 0, '', 0, 'Довожу до Вашего сведения, что (дата) на горячую линию обратился клиент (ФИО, тел.) с жалобой на ПЗ. Клиент обратился в указанный ПЗ (дата) в 12:00 по предварительной записи для сдачи биоматериала на исследование «Коронавирус». Клиент недоволен тем, что сотрудник отказала клиенту в приеме биоматериала, объяснив отказ тем, что ПЗ закрыт под обслуживание корпоративных клиентов. Клиент просит разобраться и принять меры. Ждет звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(8, 'БН-294', 10, 0, '', 0, 'Довожу до Вашего сведения, что (дата) на горячую линию обратилась клиент (дата) с жалобой на ПЗ.  Клиент обратилась  в указанный ПЗ (дата) до 12:00 для сдачи биоматериала.   Клиент недовольна тем, что сотрудник отказала клиенту в приеме биоматериала, объяснив отказ тем, что начала обслуживание клиентов организации. Клиент просит разобраться и принять меры. Ждет   звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(9, 'БН-73', 10, 0, '', 0, 'Довожу до Вашего сведения, что (дата) на горячую линию обратилась клиент (ФИО, тел.) с жалобой на оператора контакт-центра.  Клиент (дата) обратилась на горячую линию с вопросом сдачи биоматериала на ряд исследований в г. Анапа. Оператор проинформировала клиента о том, что биоматериал на исследования она может сдать до 12:00.  Клиент (дата). в 11:50 обратился в ПЗ Анапа. Клиент недоволен тем, что в ПЗ уже велось оформление  пациента на исследование «Короновирус». Сотрудник ПЗ попросила клиента подождать на улице. Клиент повторно обратилась на горячую линию. Оператор предложила клиенту обратиться в другой ПЗ  до 14:00. Клиент недовольна тем, что в предложенном ПЗ клиенту также отказали. Клиент просит разобраться и принять меры.   Ждет звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(10, 'БН-481', 10, 0, '', 0, 'Довожу до Вашего сведения, что (дата) на горячую линию обратилась клиент (ФИО, тел.) с жалобой на выездную медсестру.  Клиент оформила услугу «Выезд медицинской сестры на дом» на (дата). Клиент для получения результата указала сотруднику адрес электронной почты. Клиент недовольна тем, что сотрудник не внесла в заказ электронную почту. По этой причине клиент не смогла получить результат. Клиент просит разобраться и принять меры. Ждет   звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(11, 'БН-188', 10, 0, '', 0, 'Довожу до Вашего сведения, что на горячую линию обратилась клиент (ФИО, тел.) с жалобой на качество обслуживания ПЗ Сочи.  Клиент дважды обращалась  в указанный ПЗ 30.04.2020 г. для сдачи биоматериала и 04.05.2020 г. за результатом исследования. Клиент недовольна тем, что сотрудник указанного ПЗ позволяет себе общаться с клиентом грубо и неприветливо. Также сотрудник грубо отказывала клиенту в просьбе посетить санузел и клиенту пришлось ругаться с сотрудником.  Клиент считает подобное отношение недопустимым.  Клиент просит разобраться и принять меры. Ждет   звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(12, 'БН-494', 10, 0, '', 0, 'Довожу до Вашего сведения, что на горячую линию обратилась клиент (ФИО, тел.) с жалобой на ПЗ Ейск.  Клиент обратилась в указанный ПЗ для сдачи биоматериала на исследование «Коронавирус». Клиент недовольна тем, что сотрудник ПЗ сфотографировала паспорт клиента на личный телефон. Клиент считает это нарушением. Клиент просит разобраться и принять меры.   Ждет звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(13, 'БН-906', 10, 0, '', 0, 'Довожу до Вашего сведения, что на горячую линию обратился клиент (ФИО, тел. ) с жалобой на задержку выдачи результата.  Клиент обратился  в   ПЗ  для сдачи биоматериала на исследование «Коронавирус».   Клиент недоволен тем, что 13.05.2020 г. исследование  не было выполнено. Клиент считает, что лаборатория нарушает сроки выполнения исследования – 2 рабочих дня. Клиент просит разобраться и принять меры. Ждет   звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:45', 1, NULL, 0),
(14, 'БН-49', 10, 0, '', 0, 'Довожу до Вашего сведения, что на горячую линию обратилась клиент (ФИО, тел) с жалобой на ПЗ.  Клиент обратилась в указанный ПЗ для сдачи биоматериала на исследование «Коронавирус».  Клиент недовольна тем, что при оформлении заказа сотрудник сфотографировала паспорт на свой личный телефон и отправила фотографию. На просьбу клиента показать инструкцию, в которой прописано, что клиент имеет право фотографировать паспорт на личный телефон, сотрудник ничего не смогла ответить. Со слов клиента, сотрудник «в грязной зоне» по правилам отсканировала паспорт клиента. Сотрудник предупредила клиента о том, что программа зависает и фотография может не получиться. Если фотография не получится, то анализ не будет выполнен.   Клиент просит разобраться и принять меры.   Клиент просит срочную обратную связь от администрации для уточнения информации прошло ли оформление заказа.', 0, '', '', '', NULL, '2022-04-10 09:50:46', 1, NULL, 0),
(15, 'БН-527', 10, 0, '', 0, 'Довожу до Вашего сведения, что на горячую линию обратилась клиент (ФИО, тел) с жалобой на ПЗ.  Клиент обратилась в указанный ПЗ для сдачи биоматериала.  Клиент недовольна тем, что сотрудник сфотографировала паспорт клиента на свой личный телефон. Клиент считает грубым нарушением то, что в личном устройстве сотрудника хранятся персональные данные клиента. Клиент просит удалить свои данные в устройстве сотрудника. Клиент считает, что  филиалы должны быть оснащены специальной техникой для сканирования документов.  Клиент просит разобраться и принять меры.   Ждет звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:46', 1, NULL, 0),
(16, 'БН-487', 10, 0, '', 0, 'Довожу до Вашего сведения, что на горячую линию обратилась клиент (ФИО, тел) с жалобой на ПЗ.  Клиент  обратилась в указанный ПЗ для сдачи биоматериала на гистологическое исследование соскоба из полости матки своей маме  ФИО. Сотрудник проинформировала клиента о том, что анализ будет готов (дата). Клиент для уточнения информации обратилась на горячую линию. Оператор проинформировала клиента, что указанное исследование выполняется в течение 10 дней. Клиент просит разобраться и принять меры.   Ждет звонка администрации.', 0, '', '', '', NULL, '2022-04-10 09:50:46', 1, NULL, 0),
