--
-- Триггеры `ur_invoice`
--
DELIMITER $$
CREATE TRIGGER `ur_inv_ad` AFTER DELETE ON `ur_invoice` FOR EACH ROW BEGIN
  DELETE FROM ur_invoicepay where idinvoice = OLD.id;
	
  UPDATE ur_lesson SET idinvoice = 0 WHERE idinvoice = OLD.id;
  CALL ur_sportsmen_calc_saldo(OLD.sportsmen);
  CALL ur_invoice2pay(OLD.`sportsmen`, 0, 0, 0);
  #CALL ur_invoicepay_recalc(OLD.`sportsmen`);
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `ur_inv_ai` AFTER INSERT ON `ur_invoice` FOR EACH ROW BEGIN
	IF (NEW.`sum` > 0) THEN
		CALL ur_sportsmen_calc_saldo(NEW.`sportsmen`);
		#CALL ur_invoicepay_recalc(NEW.`sportsmen`);
		CALL ur_invoice2pay(NEW.`sportsmen`, NEW.`sum`, NEW.`id`, 0);
	END IF;
	IF NEW.`duedate` IS NOT NULL THEN
		CALL ur_invoice2lesson(NEW.`sportsmen`, NEW.`id`, NEW.`typeinvoice`, NEW.`dateinvoice`, NEW.`duedate`, NEW.lesson_amount);
	END IF;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `ur_inv_au` AFTER UPDATE ON `ur_invoice` FOR EACH ROW BEGIN
	IF (NEW.`sum`<>OLD.`sum`) THEN
		DELETE FROM ur_invoicepay where idinvoice = OLD.id;
		CALL ur_sportsmen_calc_saldo(NEW.`sportsmen`);
		IF (NEW.`sum` > 0) THEN
			CALL ur_invoice2pay(NEW.`sportsmen`, NEW.`sum`, NEW.`id`, 0);
		ELSE
			CALL ur_invoice2pay(NEW.`sportsmen`, 0, 0, 0);
		END IF;
		#CALL ur_invoicepay_recalc(NEW.`sportsmen`);
	END IF;
	IF (NEW.`duedate` <> OLD.`duedate` OR NEW.dateinvoice <> OLD.dateinvoice OR  NEW.lesson_amount <> OLD.lesson_amount) THEN
		UPDATE ur_lesson SET idinvoice = 0 WHERE idinvoice = OLD.id;
		IF NEW.`duedate` IS NOT NULL THEN
			CALL ur_invoice2lesson(NEW.`sportsmen`, NEW.`id`, NEW.`typeinvoice`, NEW.`dateinvoice`, NEW.`duedate`, NEW.lesson_amount);
		END IF;
	END IF;
END
$$
DELIMITER ;

